insert into coffee_beans values(22,'향긋+상큼+은은한 맛',15000,100,100,'화사한_은은한','가격을 낮추기 위한 블렌딩은 이제 그만 원하는 맛을 찾아가는 블렌딩으로 한국인들의 커피 사랑 한층 더 업그레이드! 플썸 맞춤 블렌딩으로 상품에 없는 원하시는 맛도 요청해보세요. 내추럴 베이스의 짙은 향기와 바디감에 단맛 밸런스의 라이트 로스팅을 진행해 복합적인 꽃과 과일의 향기와 미디엄 산미, 미디엄 바디를 느끼고 은은하게 이어지는 에프터를 경험할 ...','https://d2wosiipoa41qn.cloudfront.net/FtBjUrWvlCrEOcTM80cR5HNcS5I=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221125/5204736aefb64a01aca91963e78c30af.png');
insert into coffee_beans values(23,'부들+묵직+긴 여운의 맛',15000,100,100,'달콤_고소','가격을 낮추기 위한 블렌딩은 이제 그만 원하는 맛을 찾아가는 블렌딩으로 한국인들의 커피 사랑 한층 더 업그레이드! 플썸 맞춤 블렌딩으로 상품에 없는 원하시는 맛도 요청해보세요. 구운 견과류의 고소함과 바닐라의 부드러움 그리고 시간이 지날수록 입 안 가득 전해지는 다크초콜릿, 카카오의 묵직한 풍미의 황금비율 블렌딩 커피를 느껴보세요. 20g은 드립백...','https://d2wosiipoa41qn.cloudfront.net/ZEXoGLFGYxJKJtE6S2oJxebXfcI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221125/7a3eed29c35640fa912e85f7f026838e.png');
insert into coffee_beans values(24,'산뜻+달달+고소(부들)한 맛',15000,100,100,'긴여운_화사한','가격을 낮추기 위한 블렌딩은 이제 그만 원하는 맛을 찾아가는 블렌딩으로 한국인들의 커피 사랑 한층 더 업그레이드! 플썸 맞춤 블렌딩으로 상품에 없는 원하시는 맛도 요청해보세요.  싱글 오리진에서 느낄 수 없는 누구나 즐길 수 있는 적당히 낮은 산미와 초콜리티한 달콤함, 구운 견과류의 고소함, 우유와 브라운슈가 같은 부드러움이 합쳐진 황금비율 블렌딩 커...','https://d2wosiipoa41qn.cloudfront.net/gU8-a7whRO0sykAIYJ3x6Q_8wFI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221125/7ecccd42d9464028ba96ee76c46dfb82.png');
insert into coffee_beans values(25,'하와이 코나 엑스트라 팬시',13000,100,100,'풍부한_묵직한','3대 커피  아로마 : 꽃과 과일이 뒤섞인 복잡하고 향긋한 아로마 산미 : 청귤, 오렌지, 만다린 뉘앙스의 로우 산미 바디 : 우유 폼 같이 부드럽고 크리미한 바디 에프터 : 클린하고 은은하게 지속되다가 점점 강하게 느껴지는 베리맛 밸런스 : 전체적으로 로우한 인텐스의 맛이지만 향긋함이 오래 지속되며 밸런스 잡힌 맛의 변화가 있습니다. 스코어 : 87...','https://d2wosiipoa41qn.cloudfront.net/NcS1kn7fkM3nHd5KPcDFSZfsiWE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221125/b915ed27ebd149bbb73f43aa1baf4f1a.png');
