insert into coffee_beans values(78,'밝은 블렌드',13900,100,100,'상큼_밸런스','달콤한 과일의 산미와 카라멜의 단맛 고소한 후미가 느껴지는 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/55Otkb94nt52wlTzmqAq1gsAo-4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221108/75d76b50e9ce42df8d8d0eeddfb8e751.png');
insert into coffee_beans values(79,'소소하게 블렌드',12000,100,100,'견과류_묵직한','곡물과 견과류의 고소한 향미와 중간이상의 바디감 산미가 적은 커피로 로스팅한 대중적인 향미의 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/zhYvb8_l5x-dF27F5HeQdS2aA6s=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221108/f6a28ded370547d6b54e44726a8ed59d.png');
insert into coffee_beans values(80,'짙은 블렌드',12000,100,100,'초콜릿_밸런스','구운 견과류의 고소함, 짙은 초콜릿의 쌉쌀한 단맛, 은은하게 느껴지는 산미가 균형감을 유지하며 식을때까지 부드럽게 지속되는 데일리 커피 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/LXdQRrjmTvv6skGXy6-rhmZuiMw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221108/f37370d86f1742acb83e2224083a2d8a.png');
insert into coffee_beans values(81,'하우스블렌드',12000,100,100,'견과류_밸런스','땡큐로스터스 하우스 블렌드는 구운 견과류의 풍미가 좋고 깔끔한 단맛이 잘 어우러진 커피입니다. 고소한 풍미와 적당한 무게감으로 데일리 커피로 부담없이 즐기시기 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/a09iGgQv-6cf11YoVYtE9UWjZEw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221110/482c6b58cd984469add655a8e1c0156d.png');
insert into coffee_beans values(82,'쏘굿블렌드',12000,100,100,'달콤_묵직한','다크 로스팅을 거친 SO GOOD 블렌드는 구운 아몬드의 깊은 풍미와 카라멜, 흑설탕을 졸인 듯한 매끈하고 진한 단맛이 인상적인 커피입니다. 다크 초콜렛을 한 입 베어문듯한 긴 여운을 커피에서도 느껴보실 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/wC7NyemJ4yHwuIzSZmsI_szcv0M=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221110/01a0dac39fb94b76b1f46b784aacfa04.png');
insert into coffee_beans values(83,'일랑일랑 블렌드',12900,100,100,'화사한_허브','과일 티를 마시는 듯 한 상큼하고 깔끔한 단맛 꽃 향의 여운이 길게 남는 커피','https://d2wosiipoa41qn.cloudfront.net/4fXoARVzGCFwn0e0nE9eZ97yAB0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221208/7496e4491aa54b0a9beebd6ebe7e1658.png');
insert into coffee_beans values(84,'웰컴블렌드',12000,100,100,'달콤_고소','편안한 커피 한잔으로 시작하는 하루를 선물합니다. 견과류의 고소함과 단맛은 누구나 이 커피를 즐기기에 충분합니다. 밀크초콜릿, 절제된 산미 그리고 긴 여운이 특징이며 블랙,화이트 어느 조합으로도 잘 어우러지는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/1397-ZyMXwa4eMe7F9YjtzSSxzM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221110/27f8b923e10442fdb84aad9e66709eae.png');
insert into coffee_beans values(85,'허니서클 블렌드',15000,100,100,'고소_밸런스','씨리얼의 고소함과 메이플시럽 같은 단맛이 잘 어우려져 있으며 끝 맛은 깔끔해 누구나 좋아할 커피','https://d2wosiipoa41qn.cloudfront.net/62RrayIoDuVnMoWmKBqBlMNdq2g=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221208/f240cba46fb648188191f9d200cefee1.png');
insert into coffee_beans values(86,'웡카 블렌드',15900,100,100,'고소 _초콜릿','견과류의 고소한 맛과 다크 초콜릿의 묵직한 단맛이 특징이며 향미가 풍부하고 여운이 길게 남는 커피','https://d2wosiipoa41qn.cloudfront.net/wjAtPoN3CrKXVK6EBjtYNNOClTk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221208/23d8a97c556840c0b78f38ce684029ea.png');
insert into coffee_beans values(87,'땡큐블렌드',12500,100,100,'과일_초콜릿','농도감 있는 단맛을 바탕으로 한 훌륭한 밸런스를 가진 커피입니다. 좋은 커피와 맛있는 커피의 기준이 확실하진 않지만 절제된 캐릭터의 커피를 보여드리고 싶었습니다.','https://d2wosiipoa41qn.cloudfront.net/vJPlBZe8Es4kh8mFyTL_8i_jphM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221110/24bf059cd7824fa3a79b0ba37c74fddb.png');
insert into coffee_beans values(88,'콜롬비아 후일라 수프리모',13500,100,100,'달콤_밸런스','은은한 산미와 볶은 견과류의 고소한 느낌,밀크 초콜렛 같은 단맛이 조화롭습니다. 묵직한 바디감과 밸런스가 좋은 커피 입니다.','https://d2wosiipoa41qn.cloudfront.net/Koo4v7Oak5DhBSmlonXNZbrpxNI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221107/b5b016815b2145ee867a73fac18e970c.png');
insert into coffee_beans values(89,'꼬수운돌솥밥 블렌딩',13900,100,100,'쌉살_고소','꼬수운 돌솥밥은 백박집에서 나오는 돌솥밥을 모티브로 제작되었습니다. 쌉싸름한 단맛이 지배적으로, 확실하게 꼬수운 느낌이 잘 맞는커피로 특이점으로는 한가지 브라질 생두를 2가지 로스팅 포인트로 볶은 후 블렌딩을 한 점입니다. 강배전한 브라질에선 진한 쓴맛과 단맛이 중강배전 브라질에선 견과류의 고소함이 폭발적으로 표현됩니다. 땅콩같은 고소함은 은은하며...','https://d2wosiipoa41qn.cloudfront.net/XGb7Pjlesg7f_u6J6KIsGF_mVFI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221104/2905f301701f46239c13ce2ba07fec17.png');
insert into coffee_beans values(90,'콜롬비아 루이스 아니발 게이샤 워시드',13900,100,100,'꽃향기_은은한','자스민 차처럼 꽃 향이 은은하게 퍼져요. 레몬 그라스의 상쾌한 산미가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/-bke4HcZaUjqP2do5GebnX2Ng30=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221104/209d3fe0bbe94cffbe1b139ab6156865.png');
insert into coffee_beans values(91,'디카페인 에티오피아 짐마',13900,100,100,'초콜릿_달콤','무겁지 않은 바디감과 카라멜의 단맛,청포도와 같은 가벼운 산미가 주는 균형잡힌 디카페인 커피로 부드러운 목넘김이 특징인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/q-1VrZ8CwfJ8zc9IgNZzgKJcwnA=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221103/051400f2cf11467ab4bfe6fb1d41126c.png');
insert into coffee_beans values(92,'에티오피아 고로',19000,100,100,'과일_꽃향기','새콤한 과일의 느낌이 지배적이고, 은은한 단맛과 깔끔한 애프터 테이스트가 매력적인 커피','https://d2wosiipoa41qn.cloudfront.net/HN3VJf19ape2gNqtXMvq7TVxElU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221101/be81785b501e4629a70bccfe3cb96d55.png');
insert into coffee_beans values(93,'과테말라 SHB 스위스워터 디카페인',19000,100,100,'고소_견과류','곡물향, 달콤한 카라멜향, 쌉쌀하면서 부드러운 맛','https://d2wosiipoa41qn.cloudfront.net/S2AVRlQ-Oax7fymiFACs0j0LcXg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221031/8eda77fa26394b6fb5c40d843d0ed532.png');
insert into coffee_beans values(94,'블렌드 애플 레드',19000,100,100,'밸런스_부드러운','단맛과 고소함, 산미 어느 하나 빠지지 않는 밸런스 좋은 커피입니다. 사과처럼 강하지 않고 부드러운 산미가 특징인 애플 레드는 어떤 메뉴, 어떤 디저트와 함께해도 어울리는 만능 블렌드입니다. 비정제 설탕의 감칠맛과 밀크 초콜릿처럼 단맛과 쌉쌀함의 부드러운 조화가 하루를 특별하게 만들어줍니다. 특별하지만 튀지 않는 담백한 매력을 가진 달콤한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/MotZjL9_qWOrXGeTbsdtzvC0jj4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221031/3cc2294a88ef471faa76dd28ba63386a.png');
insert into coffee_beans values(95,'블렌드 너티 퍼플',8900,100,100,'고소_견과류','대중적인 입맛에 맞지만 더욱 풍부한 향과 부드러움을 내는 블렌드입니다. 보리차와 같은 고소한 맛을 내며 콜롬비아의 감칠맛과 브라질의 단맛을 적절히 블렌드하여 균형감이 좋습니다. 풍부하고 부드러운 크레마는 보기만해도 입맛이 돕니다. 산미는 거의 없으며, 아메리카노나 라떼 베리에이션 등 고소함을 강조하고 싶을 때 사용하기에 좋습니다. 호불호 갈리지 않는 ...','https://d2wosiipoa41qn.cloudfront.net/i_KU7eSFQeIlSmq5MQ9DXqJ4KYE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221031/b14ede69542d47c5ab68976cedec6068.png');
insert into coffee_beans values(96,'블렌드 캔디 그린',7800,100,100,'꽃향기_깔끔한','리프커피의 시그니처 블렌드로 기분 좋은 산미와 달콤함을 느낄 수 있는 블렌드입니다. 꽃과 과일의 향이 풍부한 캔디 그린은 혀에 닿았을때 부드럽게 느껴지는 감귤류의 산미와 뒤에 따라오는 카라멜, 갈색설탕의 감칠나는 단맛의 균형이 매력적입니다. 깔끔한 향과 질감, 오래도록 입안을 맴도는 단맛의 여운은 마치 맛있는 사탕을 입안에 물고있는 것 같은 느낌입니다...','https://d2wosiipoa41qn.cloudfront.net/2EZv6jr87PokiABPL9OuEngwBRM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221031/fcbb0b1f196e465a93974146ab2e214a.png');
insert into coffee_beans values(97,'스너그 하우스 디카페인 블렌드',18500,100,100,'달콤_고소','"Snug house" 블렌드는 편안하고 포근한 집을 상상하게 합니다. 지친 하루를 끝내고 집으로 돌아왔을 때 맛있는 음식과 포근한 침대가 반겨주듯이 부담스럽지 않은 달달하고 고소한 향미들이 오롯이 편안하고 포근한 휴식을 취할 수 있도록 도와줍니다.','https://d2wosiipoa41qn.cloudfront.net/hLaolzzLiat08VNQUYliijqBYbw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221028/d55dc3b126174cba947d4fda9c8e8f52.png');
insert into coffee_beans values(98,'나이트폴 블렌드',17000,100,100,'과일_꽃향기','"Nightfall" 블렌드는 해질녘에 인적 드문 해안가로 나와 노을이 만드는 다채로운 색감의 바다를 바라보는 것 같습니다. 노을빛 같은 다채로운 과일의 향미와 조용히 다가오는 달달한 여운이 아쉬웠던 하루의 소소한 여유를 선물합니다.','https://d2wosiipoa41qn.cloudfront.net/wZ9WN4wtnpKOqMMTCYmqDXIyFyE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221027/e828cfb6afd74dad91bb2edfea48bb31.png');
insert into coffee_beans values(99,'포레스트 블렌드',17000,100,100,'고소_견과류','"Forest" 블렌드는 마시는 이에게 시원한 바람과 기분 좋은 향기를 느낄 수 있는 편안한 숲속을 상상하게 합니다. 땅에서 재배되는 고소한 견과류의 향미와 달달한 여운이 좋은 균형감을 가지고 있어 기분 좋은 하루의 시작과 함께합니다.','https://d2wosiipoa41qn.cloudfront.net/dllK79UPMZTpr0Z8Qee6wFk0HDk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221027/99d9566f08e1448991a6a468cfa69b93.png');
insert into coffee_beans values(100,'문나이트 블렌드',15000,100,100,'묵직한_초콜릿','"Moonlight" 어두운 밤 하늘에 은은히 빛나는 달빛을 닮았습니다. 어둡지만 달달한 초콜릿스러운 바탕에 바닐라의 기분 좋은 여운이 은은하게 퍼지며 지친 하루의 끝에 달달하고 편안한 밤을 선물합니다.','https://d2wosiipoa41qn.cloudfront.net/dllK79UPMZTpr0Z8Qee6wFk0HDk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221027/99d9566f08e1448991a6a468cfa69b93.png');
insert into coffee_beans values(101,'에티오피아 쿠쿠세 내추럴',11500,100,100,'과일_달콤','딸기와 유자의 과일같은 풍부한 맛이 느껴져요 라벤더 꽃 향기가 멤돌아요','https://d2wosiipoa41qn.cloudfront.net/IxMQqqv792yRJWpTsLNDGm4vuEI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221027/16b977d4c21d4c299fd7aabe71393c00.png');
insert into coffee_beans values(102,'과테말라 엘 피날 게이샤 워시드',12000,100,100,'달콤_화사한','잘 익은 달콤한 사과가 떠올라요 화사하게 입안에 여운이 남아요','https://d2wosiipoa41qn.cloudfront.net/2EUiVyf9aNwTCJZaX-OKt_ODOeQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221027/78bddd1cafb14ff28c73382405e6783f.png');
insert into coffee_beans values(103,'블렌드 루트 블랙',13000,100,100,'초콜릿_밸런스','리프커피의 시그니처 블렌드로 중후한 단맛과 쌉쌀함을 느낄 수 있는 블렌드입니다. 카카오 함량이 높은 고메 초콜릿을 마시는 듯한 풍부한 맛입니다. 입 안에 묵직하게 느껴지는 질감과 견과류의 단맛, 부드러운 목넘김이 매력적입니다. 단맛과 쌉쌀함의 밸런스가 좋아 처음부터 끝까지 편안하게 마실 수 있습니다. 아주 살짝 느껴지는 산미는 맴돌았던 향미를 정돈하며...','https://d2wosiipoa41qn.cloudfront.net/rVMnWhV2oiVUAbJF9qRSjuV5x-g=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221031/acb2db695ddb43c09bfd7c6a91d695c5.png');
insert into coffee_beans values(104,'에티오피아 시다마 파피초 내추럴',13600,100,100,'과일_꽃향기','장미꽃 향기, 풍부한 열대 과일의 맛과 향이 크리미하게 입안에서 느껴집니다. 네추럴 특유의 과한 발효향이 없고 부드럽습니다.','https://d2wosiipoa41qn.cloudfront.net/kUUbIkZi-pIGl0b4VGAESm09x6A=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221026/5528d0656b6247a0968a2b0aa8cb1467.png');
