insert into coffee_beans values(136,'다크초콜릿 블렌드',18000,100,100,'고소_묵직한','다크로스팅 특유의 진한 느낌과 쌉싸름함이 느껴지지만 쓰지않고 깨끗한 단맛이 특징입니다.','https://d2wosiipoa41qn.cloudfront.net/mwOVm6IJD9RhCOjA5Ohqh-phtbQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220718/27428273f41943f0a1c46037bce969ab.png');
insert into coffee_beans values(137,'초콜릿 블렌드',19000,100,100,'밸런스_묵직한','이름처럼 초콜릿, 카카오닙스의 향이 특징적이며 긴 단맛이 애프터로 남는것이 매력적입니다.','https://d2wosiipoa41qn.cloudfront.net/6hgpipX-NKg38AtO2dFaq4IcA4U=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220718/9787469f0918418fb4e62d0d6f02d67c.png');
insert into coffee_beans values(138,'케냐 AA 워시드',19000,100,100,'상큼_부드러운','자몽과 라임 그리고 토마토의 향미가 조화롭게 어울려 상큼하고 부드러운 커피입니다. 밸런스가 좋으며 매우 깔끔합니다.','https://d2wosiipoa41qn.cloudfront.net/J3fTuA4UmWTUeDwnuMccS8j_NBA=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220718/fb94c4be52f54409b5281d7662448b26.png');
insert into coffee_beans values(139,'코스타리카 엘 베네피시오 블랙 허니',12000,100,100,'달콤_부드러운','블랙체리와 자두와 같은 과일의 고급스러운 단맛과 향이 특징인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/-rJyPe17nx_RBXnWPEG-5r7NKD8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/2790575fbb4d4d1e832c74017f8a22e1.png');
insert into coffee_beans values(140,'에티오피아 시다마 벤사 케라모 워시드',12500,100,100,'상큼_깔끔한','오렌지 상큼한 향과 깔금하고 고급스러운 단맛이 느껴지는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/uZ_LtCxJnJG5_jpKzIQribkrjOU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/a044e2f37a5d4658b38333b47014bff6.png');
insert into coffee_beans values(141,'W 블렌드',12500,100,100,'쌉살_묵직한','깊은 바디감과 후미에 중점을 둔 강배전의 블렌딩 원두입니다. 다크초콜릿과 스모크 향이 만나서, 깊은 바디감을 만들며 카카오 닙스의 쓴맛을 가진 것이 특징입니다.','https://d2wosiipoa41qn.cloudfront.net/po58_dGukMg2WMBicJLdhUd-cqI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/336e8b868414403b800a89237e88be66.png');
insert into coffee_beans values(142,'F 블렌드',12000,100,100,'고소_달콤','단맛과 풍미에 중점을 둔 중강배전의 블렌딩 원두입니다. 견과류의 고소함과 캐러멜, 사탕, 다크초콜릿의 단맛, 후미의 쌉싸름함까지 복합적인 단맛을 가진 것이 특징입니다.','https://d2wosiipoa41qn.cloudfront.net/qHOmmrBXGCOdv9u0W2dO7W7Z81E=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/0e7838f608c343849baec84b9baafb1d.png');
insert into coffee_beans values(143,'R 블렌드',14000,100,100,'밸런스_달콤','밸런스에 초점을 맞춘 중배전의 블렌딩 원두입니다. 달달한 꿀과 캐러멜스러운 단맛에 꽃 향이 맴도는 은은한 산미가 매력적입니다.','https://d2wosiipoa41qn.cloudfront.net/gKIzPPZZCT3E3UOclFdmr8M_WEU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/3b7aa9750ff64b11935e65ae7701abf1.png');
insert into coffee_beans values(144,'카라멜 슬럽',15500,100,100,'고소_부드러운','밀크초콜렛을 머금고 있는 듯한 부드러운 질감에 은은하게 오는 산미 주변 상권보다 특별한 맛을 찾는 사장님에게도 추천드립니다.','https://d2wosiipoa41qn.cloudfront.net/tDTBijlSjVBPKtWRKbSLFWR_BIE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/de68cd9e2e174abcb84a1af2b11ac2d2.png');
insert into coffee_beans values(145,'나비',13700,100,100,'꽃향기_과일','깨끗하고 깔끔하며 오렌지 주스를 마시는 것 같은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/E2ph1b2mueHFaDDEP_71upP4s7g=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220628/dd3d64f2b6f6429882aed3a2978f1f0c.png');
insert into coffee_beans values(146,'몽상스',18900,100,100,'밸런스_달콤','목넘김이 매끄러워 술술 넘어가는, 달고 부드럽고 바디가 좋은 케냐 커피입니다. 특히 라떼로 맛있습니다.','https://d2wosiipoa41qn.cloudfront.net/j6B06nVC6XGzdNH9Ks_Ixi-yGTI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220628/8276db51aff94f1bb4f7e86c790d2a17.png');
insert into coffee_beans values(147,'와이칸 다크',18000,100,100,'초콜릿_고소','다크초콜릿, 블랙체리, 솔티드카라멜의 쌉쌀하고 단 맛이 일품이며 뒷 여운이 깔끔해요.','https://d2wosiipoa41qn.cloudfront.net/E3_OWWbkH4l9lXq_e8LElOQaORg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220628/8f24dccb7d6646f6b16eaf55054542f5.png');
insert into coffee_beans values(148,'블렌드 슈퍼 노멀',18500,100,100,'밸런스_쌉살','묵직하되 그 묵직함이 과하지 않고 선명한 단맛을 지녔습니다. 다크한 초콜릿 맛과 미미한 산미가 좋은 밸런스를 느끼게 합니다. 데일리 커피로서 역할을 잘 해내리라 자부합니다.','https://d2wosiipoa41qn.cloudfront.net/iI_svDBt-hPYoZZ_Cch7_z2QKkY=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220615/dd651f1b67854e54b068bdf87f04c22c.png');
insert into coffee_beans values(149,'인도 바드라 아라비카',13900,100,100,'초콜릿_묵직한','초콜릿의 달콤함과 호두의 고소함의 조화가 묵직한 바디감과 어우러져있는 매력적인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/OLzDTGACggXTXIExhB1vBvf9eEM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220613/cd5ede0e2fef46b7b00cb97f5fc18769.png');
insert into coffee_beans values(150,'케냐 구아마 AA',13900,100,100,'달콤_상큼','블랙베리와 체리톤의 향미와 적포도,조청 등의 단맛이 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/nkJI7rcQHct-dhD6K0zy_wVkRyE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220524/6f8cb35b4efe446db590a40d9047b506.png');
insert into coffee_beans values(151,'브라운 블렌딩 커피',15900,100,100,'쌉살_묵직한','산미가 거의 없는 진한 다크초콜릿 음료같아요. 풍부한 바다김과 적당한 쌉쌀함이 잘 어울려요.','https://d2wosiipoa41qn.cloudfront.net/Oviva-UVnz8WvE1dJPHVkB1DwJE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/ea6a1bb767f844678d8b4d70c858b524.png');
insert into coffee_beans values(152,'그린 블렌딩 커피',15900,100,100,'밸런스_깔끔한','부드러운 단맛과 고소한 풍미가 밸런스가 좋습니다. 좋은 균형감이 매일 마시기 편안한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/WFEUcOkcSduVCcBOTbG3uMuukIU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/e71e4aaa815445a8ba83a5c0d72643ed.png');
insert into coffee_beans values(153,'콜롬비아 디카페',11000,100,100,'밸런스_견과류','산뜻한 건과일 플레이버 뒤로 이어지는 토피넛과 코코아의 달콤함으로, 안정된 밸런스를 가진 디카페인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/fHLs6lHL5c3DO4D_hLA8adY9Pjo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220907/29b87f9baa2041a8835bdf44739da4dc.png');
insert into coffee_beans values(154,'데일리뷰티 블렌',12500,100,100,'초콜릿_달콤','은은하게 퍼지는 산미와 초콜릿의 단맛이 느껴져요','https://d2wosiipoa41qn.cloudfront.net/w_bXMdBzeisftK429Ox9XpIhgkU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/e221d21c0d98420ebcc62211e26fd186.png');
insert into coffee_beans values(155,'디카페인 콜롬비아',12000,100,100,'고소_부드러운','초콜릿과 황설탕의 단맛이 느껴지고 좋은 밸런스를 가지고 있습니다.','https://d2wosiipoa41qn.cloudfront.net/0epdTEZniX8ow2RwN-xyuuq-b6o=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/0ee55b7e84534981b13ccbba545fae3a.png');
insert into coffee_beans values(156,'모카 1959 CM',13000,100,100,'과일_달콤','과일의 상큼함과 화이트 초콜릿의 단맛이 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/QfmJ3FK4LBWBCVOKkPq_u_Y6Aig=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/6b846626142f4adfae7a5f9adae2b28e.png');
insert into coffee_beans values(157,'모카 돈 엘킨',14000,100,100,'고소_꽃향기','마카다미아, 라즈베리, 장미, 티라이크 _ 밀크초콜릿의 맛을 느낄 수 있는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/f5Y2jB5iNNNDihlkAmtZGPy2QVk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/79e1917a296149c39e9f5173789cb151.png');
insert into coffee_beans values(158,'BEIGE 블랜드',15000,100,100,'꽃향기_깔끔한','오렌지쥬스 같은 질감과 화사하고 가벼운 느낌의 바디감, 가볍게 하루를 깨우고 싶으시다면, 이 원두와 함께 시작하시는걸 굉장히 추천드립니다.','https://d2wosiipoa41qn.cloudfront.net/Zfpf8XTLteJZI-cXfLBBuvFzKQU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/095c6f6320724e808d39ec514d154dcd.png');
insert into coffee_beans values(159,'BRICK 블랜드',15000,100,100,'달콤_고소','처음에 입에 닿을때 느껴지는, 다크초콜릿의 풍미와 기분 좋게 느낄수 있는 묵직한 바디감 그리고 후미에 올라오는 달달함으로 블랙으로 드셔도, 우유와의 조합으로 드셔도 정말 좋은 블랜드입니다.','https://d2wosiipoa41qn.cloudfront.net/9h9LHKfj2U0FN7PKlBppcQ1I4mE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/93ef4ceecf094852a32a79b363544823.png');
insert into coffee_beans values(160,'피크 블렌드',16000,100,100,'달콤_밸런스','처음부터 끝까지 편안하게 마실 수 있어요.','https://d2wosiipoa41qn.cloudfront.net/0fyLtVbKCpasNk0UT8ORp9xmXiM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/11ffc55b35544b60811ea8fbf9c047d9.png');
insert into coffee_beans values(161,'오드 블렌드',12500,100,100,'달콤_밸런스','달달한 단감의 단맛이 길게 이어지는 커피로 끝 맛은 진한 홍차처럼 느껴져요.','https://d2wosiipoa41qn.cloudfront.net/lw9zQ8cBqkvXNcL_J2UNMjumZ2E=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/bdba1e9747e64289a846ae017af7378a.png');
insert into coffee_beans values(162,'이븐 블렌드',12900,100,100,'고소_묵직한','고소한 견과류와 묵직한 초콜릿 단맛이 느껴지는 커피로 여운이 길고 밸런스가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/mv6gKjTqTfEyeyIMyzgVNRINJ5Y=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/22b2bc8b51e94d32be190b339dc92b8d.png');
insert into coffee_beans values(163,'코스타리카 SHB EP 피에드라 알타 내추럴',12900,100,100,'과일_은은한','잘 익은 포도와 크랜베리의 향미가 느껴지는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/1DzoKXecDDZhp_R0I8oifWRnU_E=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/c9bdbe7e988e48869ca58a90e3e00838.png');
insert into coffee_beans values(164,'콜롬비아 호르헤 에르난도',14000,100,100,'과일_깔끔한','오렌지와 크랜베리 주스의 새콤 달콤함에 매끄러운 촉감까지 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/ggS0VV6p0ZgEWCV8QRZryzasw68=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/d997d7759a7f4a56a9f24c0ba7706a24.png');
insert into coffee_beans values(165,'돌담in애월',13000,100,100,'밸런스_묵직한','매서운 제주의 바닷바람을 묵묵히 막아주는 돌담의 모습을 형상화하였습니다. 고소하고 바디감 좋은 중남미 원두들에 르완다 이심비를 추가하여, 다크초콜렛과 흑설탕의 기분 좋은 단맛과 긴 여운을 주는 바디감, 치우침 없는 균형감을 추구합니다.','https://d2wosiipoa41qn.cloudfront.net/6-q26Jnjt_oJZpdc0yHji-kddiM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/4a040ba3e55842599a76d5f234fe4123.png');
insert into coffee_beans values(166,'하늘에 시그니처 블랜딩',13600,100,100,'상큼_깔끔한','케냐 AA를 베이스로 에티오피아 모모라 내추럴을 더하였습니다. 오렌지의 산미와 카라멜의 단맛, 은은하게 퍼지는 꽃향의 여운을 느끼실 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/Vcnh0NHeOSZV3Cyq6hQffobSSb8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220907/5573addfe91045b48a0962baceda9652.png');
insert into coffee_beans values(167,'피타 블렌드',18000,100,100,'밸런스_묵직한','고품질의 마이크로랏 생두 위주로 블렌딩하여 스페셜티의 신세계를 경험할 수 있습니다. 신맛과 고소한맛 짙은 농도의 밸런스가 잘 잡힌 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/1Cj5RrQEMEbh-AazV26tZ2CP-C8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/241055120e924bb399cc8ef1bdbdc399.png');
insert into coffee_beans values(168,'디카페인 블렌드',16200,100,100,'밸런스_초콜릿','카페인은 안녕, 풍미와 향미는 그대로 화산 빙하의 깨끗한 물과 탄소필터를 활용해 카페인만 99.9% 제거하여, 커피 본연의 풍미와 향을 해치지 않은 마운틴 워터 프로세스의 디카페인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/d1wnKHqlc0Ict0Fpv-aH8F0r6eE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/a6d77f5c639a41e7b1c398ab15dca612.png');
insert into coffee_beans values(169,'썸즈업 블렌드',12500,100,100,'고소_쌉살','다크 초콜렛의 단맛과 견과류의 고소함, 신맛이 적은 것이 특징으로, 고소한 맛을 한번에 표현하는 블렌딩입니다. 썸즈업의 달콤하고 진한 맛은 어떤 베리에이션으로도 잘 어울립니다.','https://d2wosiipoa41qn.cloudfront.net/PriOJXNyNuIBRVoAryImpaW2VwI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/cc00cef1ef34480aaad6a61b546d70ab.png');
insert into coffee_beans values(170,'헥사 블렌드',15000,100,100,'밸런스_긴여운','헥사는 밸런스에 초점을 맞춘 하우트커피의 시그니처 블렌드입니다. 진한 초콜릿향, 농후한 단맛, 부드러운 산미, 고급스러운 바디감과 실키함까지 커피가 가진 다양한 매력을 만나보세요.','https://d2wosiipoa41qn.cloudfront.net/2TFvPvusfCmfwJqKFpgu4B_R-8Y=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/7310c25d5b294ba6a03da7fb39e409c7.png');
insert into coffee_beans values(171,'44도 블렌드',14000,100,100,'달콤_밸런스','먹었을 때 느껴지는 초콜릿티한 맛과 어우러지는 호두같은 고소함이 매력적입니다. 또한 커피를 먹고 남는 여운은 스모키한 향과 맛이 기억에 남아 맛있게 먹을수 있는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/zyck3vB-rh01_GKzwQ5Jn8SAQNI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/a1dd9be4dd434a2b8e6da38289171be3.png');
insert into coffee_beans values(172,'콜롬비아 디카페인',16000,100,100,'밸런스_초콜릿','초콜릿티한 달콤함과 좋은 단맛으로 누구나 마시기 편한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/oeLS8bEeqUTpksvJmproMmEqjwk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/335ae08b395841d7828b1d2013414fb0.png');
insert into coffee_beans values(173,'피어투피어',13000,100,100,'과일_밸런스','은은한 시트러스, 핵과류의 산미와 부드러운 단맛이 좋으며, 티처럼 편하게 즐기기 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/1QHClWT7AHscG9yRfs0DgL4MVEo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/9d865d01e99f4a5c8ddc7a680fc570c1.png');
insert into coffee_beans values(174,'인도 마이소르 너겟 엑스트라 볼드',18000,100,100,'초콜릿_견과류','오일이 보일정도의 다크 로스팅입니다. 다크 초콜릿, 갈색설탕의 단맛이 좋으며, 볶은 견과류와 바디감이 풍부한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/X8nMy8QdT3W3xZITBMu-Gh4TyCM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/6066c37158fe4eb7a6b4396508330c5b.png');
insert into coffee_beans values(175,'콜롬비아 E.A 디카페인',19000,100,100,'고소_초콜릿','디카페인 특유의 향이 많이 나지 않고, 고소하고 부드러운 군고구마 뉘앙스의 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/kJ-dCXePLMDLbeyyjP0hB2YudI0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/bf2786bab34648a99367fd806ac1d754.png');
insert into coffee_beans values(176,'DCW 블렌드',19000,100,100,'밸런스_달콤','단맛과 고소한 맛을 중심으로 한 방울 떨어뜨린 듯 은은한 산미까지, 풍성한 향미가 둥글게 다가옵니다.','https://d2wosiipoa41qn.cloudfront.net/Oj1GvyE4ZTfUlrcIYjCjRmytWg4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/0ee29d398c9e42e28bf8dddf6237698f.png');
insert into coffee_beans values(177,'과테말라 SHB SW 디카프',12000,100,100,'견과류_고소','레몬그라스 같은 독특한 향에 고소한 너트 향, 달콤한 꿀 향이 섞여 풍성하게 다가옵니다.','https://d2wosiipoa41qn.cloudfront.net/zoS_TTBMWBe2NlFW9yj0ILOLKc4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/f4fcb86980424dbeb65597e87adeeb8e.png');
insert into coffee_beans values(178,'에티오피아 G2 시다모 SW 디카프',12500,100,100,'과일_초콜릿','다크초콜릿 같은 달콤쌉싸름한 맛에 오렌지과 산미가 은은하게 개성을 더합니다.','https://d2wosiipoa41qn.cloudfront.net/WZVpt7ytpOm981fgYGdZmb0QJl8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/413a58340584476097d2e925b561d64b.png');
insert into coffee_beans values(179,'콜롬비아 수프리모 SW 디카프',12500,100,100,'달콤_고소','비스킷 같은 고소하고 달콤한 향에 부담스럽지 않은 산미가 부드럽게 퍼집니다.','https://d2wosiipoa41qn.cloudfront.net/JAM62oxOGhpJa9aTNWMYlaa6CKw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/c1a787fc92984aa1920fcf06068e3d3a.png');
insert into coffee_beans values(180,'케냐 구아마 AA TOP 마이크로 랏',15000,100,100,'달콤_상큼','잘 익은 과일차를 마시는 느낌의 커피입니다. 질감이 부드럽습니다.','https://d2wosiipoa41qn.cloudfront.net/pr_UN115yBL8_AGzA4vq9MfxK18=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/a2f3280bb7714b14a7d1d1740047706c.png');
insert into coffee_beans values(181,'과테말라 와이칸 워시드',13000,100,100,'밸런스_부드러운','쓴커피를 벗어날 수 있는 핸드드립 입문자를 위한 밸런스 있는 커피입니다. 긴 잔향과 부담없는 향미로 호불호가 없는 와이칸을 즐겨보세요.','https://d2wosiipoa41qn.cloudfront.net/arc0XlL3wvyFQjwLzjT_7p5KnNU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/16e8e6916d0b478086e6145ac56ba8c6.png');
insert into coffee_beans values(182,'스탠다드룸',13500,100,100,'밸런스_견과류','미디엄 로스트의 스탠다드룸은 누구나 편안하게 즐길 수 있는 가장 스탠다드한 블렌드입니다. 2차크랙 직전 배출로 헤비한 느낌보다는 적당히 고소하며 마일드한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/DcyW_CW55HiCd7mY8orNCuCwa1Y=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/b7d3dcbd236d46f9ac210f48056774bd.png');
insert into coffee_beans values(183,'스테이',13900,100,100,'고소_묵직한','너무 과하지 않은 배전도로 적절한 쌉싸름함과 스카치 위스키의 몰티함, 초코쿠키의 달달고소함, 깔끔한 밸런스로 어떠한 형태의 커피로든 잘 어울리도록 하였습니다','https://d2wosiipoa41qn.cloudfront.net/1cF5u_FFPhc-6YKOwZ91eQCAZfg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/9de28b21d9d04f5eaed0489b833ce8e3.png');
insert into coffee_beans values(184,'노네임블렌드',12000,100,100,'달콤_고소','견과류 고소함과 다크 초코의 단맛 밸런스가 좋고, 크림 음료들과도 잘 어우러져요.','https://d2wosiipoa41qn.cloudfront.net/OfM5ZVVKfNAOGHaZJ4msTtt9Xp4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/41baff6e2a034a708e0b603481e4f11f.png');
insert into coffee_beans values(185,'수수블렌드',12000,100,100,'상큼_깔끔한','복숭아의 단맛과 산미가 깔끔하고 길게 이어집니다. 과일차처럼 부담 없이 마시기 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/emzV-vB0VkSIrgBnTa9R9mfb9lE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/c3ea20adc16f4301800a89adc76f34e2.png');
insert into coffee_beans values(186,'나폴리',12000,100,100,'상큼_깔끔한','볶은 아몬드의 고소함과 신선한 과일의 산미가 느껴져요.','https://d2wosiipoa41qn.cloudfront.net/JXdcTF2CWm2PfT1sUZohMVTI1oE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/710a6529589a4fdfb35eda6adf08b683.png');
insert into coffee_beans values(187,'하드워커',11500,100,100,'달콤_초콜릿','다크 초콜릿스러운 단맛과 묵직한 바디, 쌉싸름한 후미가 조화를 이룹니다.','https://d2wosiipoa41qn.cloudfront.net/8OD0WNDYmtbAURsEP7paZlHeiiI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/6d02c8b9239c46559089399132d51928.png');
insert into coffee_beans values(188,'콜롬비아 디카페인',12000,100,100,'밸런스_깔끔한','부드럽고 은은한 코코아를 닮은 디카페인 커피','https://d2wosiipoa41qn.cloudfront.net/-t56i37u7fC8OT0hdia61XXGFYU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/e4cae7f12b6848ae99f1397208f4d8b2.png');
insert into coffee_beans values(189,'페루 라 메르셋 게이샤 워시드',13000,100,100,'달콤_밸런스','단맛이 풍부하고 은은한 오렌지와 자스민 향이 식으면서 강해집니다.','https://d2wosiipoa41qn.cloudfront.net/z3Y7FtSsB9rOJwAcccmALcaguo4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/420e88ce93f745cabc383687c25af0fe.png');
insert into coffee_beans values(190,'콜롬비아 싱글오리진 디카페인',13600,100,100,'밸런스_달콤','카라멜, 사과, 오렌지의 달콤함과 허브류의 여운이 좋은 디카페인 입니다.','https://d2wosiipoa41qn.cloudfront.net/VG1H8AMnGG-NmBL_bmWuPzbluLc=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/9f22f7c06b594a4296c5119dc9ea7e7c.png');
insert into coffee_beans values(191,'레이크 맥켄지',18000,100,100,'상큼_깔끔한','열대과일을 연상시키는 복합적인 산미와 깨끗하고 부드러운 사탕수수의 단맛','https://d2wosiipoa41qn.cloudfront.net/1J7OplfeyMIgKHhqBn8zU1hmUL8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220802/104b951fe1ba482281e71b82d99af249.png');
insert into coffee_beans values(192,'엘리 크릭',16200,100,100,'밸런스_부드러운','풍부한 단맛과 부드러운 질감을 갖춘 밸런스가 돋보이는 매력적인 커피','https://d2wosiipoa41qn.cloudfront.net/GiIGX5FaRE3qDDwfMO4b99w12MQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/c7e54fd0150046c99e157e27bc54c2f3.png');
insert into coffee_beans values(193,'인디언 헤드',12500,100,100,'쌉살_묵직한','다크 초콜릿처럼 깊은 풍미와 단맛이 진하게 감싸는 한국형 클래식 에스프레소','https://d2wosiipoa41qn.cloudfront.net/3PCiqgLgCDPp3j_LE4Wd_LRCswQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/e1c3aabb3722407fb3e3fa703fbce898.png');
insert into coffee_beans values(194,'콜롬비아 디카페인 에스프레소',15000,100,100,'달콤_묵직한','다크 초콜릿의 단맛과 감초의 스파이시함 블랙티의 여운이 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/1Og44akPphlo_yc-NrRVX8gpd5I=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220110/70f288881c294426af466876ba263216.png');
insert into coffee_beans values(195,'브릭 에스프레소',14000,100,100,'밸런스 고소','고소한 견과류의 맛과 흑설탕의 단맛 그리고 부드러운 촉감과 완벽한 밸런스를 가진 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/tVyfB77H2ZHwk6CMFVUuze3ADD0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220110/36c1843e84d54e3aa9ab414ca6ac746c.png');
insert into coffee_beans values(196,'클래식 에스프레소',16000,100,100,'밸런스_고소','카카오를 씹은 듯 쌉싸름한 맛 뒤에 고소한 달콤함이 어우러진 묵직한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/yWR7GKLj80Jpr2Bg6VtqyiZfkgk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/219afed18f044ddd8a9239d8b7dfbbe1.png');
insert into coffee_beans values(197,'그 해 겨울',13000,100,100,'달콤_부드러운','은은한 산미, 헤이즐넛, 초콜릿의 단맛 등 복합적인 향과 좋은 밸런스를 지닌 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/mfMCMsQcapwnCJ-K18kwTHLc1jo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/c8f2ee408fc5484bbcbf785be40ac50c.png');
insert into coffee_beans values(198,'과테말라 우에우에테낭고 버번 워시드',18000,100,100,'꽃향기_밸런스','은은한 산미, 헤이즐넛, 초콜릿의 단맛 등 복합적인 향과 좋은 밸런스를 지닌 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/lA0a6Gxe5AHffA1zc1oY6KEEMqs=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221202/f55a065d1806468eb1b8244e20f4ac17.png');
insert into coffee_beans values(199,'콜롬비아 디카페인 한국커피 셀렉션',19000,100,100,'견과류_고소','견과류의 고소함, 은은한 단맛과 크림처럼 부드러운 식감이 식을 때까지 유지돼요.','https://d2wosiipoa41qn.cloudfront.net/FE9qq7O0y3gStjCCaCzZOMAWt_w=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/4a27e3e7646943c9ba981dacb0716129.png');
insert into coffee_beans values(200,'과테말라 핀카 비즈카야 버번 화이트허니 120h 이스트 발효',19000,100,100,'과일_초콜릿','금귤의 달콤상큼한 느낌으로 시작하여 식어갈수록 카카오닙차를 마시는듯한 느낌이 들 거예요.','https://d2wosiipoa41qn.cloudfront.net/yi-iSKtL8b4fQa13eWgzOPZvIr4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/ed720af46a4f46c1bcd6fa4dc22114d5.png');
insert into coffee_beans values(201,'뉴커먼',12000,100,100,'고소_묵직한','견과류의 고소함을 베이스로 깊은 바디감이 잘 어울리는 균형감이 좋은 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/-Cl5b2qZ4l9phh65k4kZnsaqS3c=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/a575d98c865b4dee96d9349af5c8a98f.png');
insert into coffee_beans values(202,'발렌타인',12500,100,100,'쌉살_묵직한','다크 초콜릿의 쌉싸름함과 단맛이 뛰어나고, 뒤에서 느껴지는 스모크함이 개성 있게 느껴지는 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/PoYzR2yi4OhU2RFo7EAv7Eq24TU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/fa97e9e1d5b847349734063c1cbb65d7.png');
insert into coffee_beans values(203,'브라이트',12500,100,100,'상큼_부드러운','복숭아, 살구와 같은 핵과의 향미와 단맛이 잘 표현되며, 부담스럽지 않은 산미가 조화로운 스페셜티 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/epQGPhOPqV0byYm8t-NFcu4lrk4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/7beaef952ff94a1f84a932ea9fbd6842.png');
insert into coffee_beans values(204,'블랙 앤 모어',12000,100,100,'쌉살_묵직한','묵직한 바디감과 긴 여운을 가지고 있어 클래식한 커피를 좋아하는 분들께 추천 드립니다.','https://d2wosiipoa41qn.cloudfront.net/jP9XCT850Xw2cT2HcznVek8NCg0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220802/7e80f8d734504a5eb6ad3806dd77815e.png');
insert into coffee_beans values(205,'스윗 앤 모어',14000,100,100,'밸런스_부드러운','밸런스가 좋고, 자극적이지 않은 클린한 커피라 누구나 무난하게 즐기기 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/KOPLHoSm-AFrtukwN2qp_1QZ2cs=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/4712c5b81b214943ba7fb0b7129fde46.png');
insert into coffee_beans values(206,'글램 앤 모',15500,100,100,'상큼_깔끔한','화사한 산미를 시작으로 체리와 포도의 달콤함을 느낄 수 있으며 스파클링의 청량감으로 입안에서 톡톡 터지는 과일 향미를 즐길 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/ZuFOfEs6_0uo_J_BYbsZyt4rrBQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/2638b403ef9b4aa0941164388352cb05.png');
insert into coffee_beans values(207,'코스타리카 핀카 마초 워시드',13700,100,100,'달콤_상큼','잘 익은 사과의 상큼함과 복숭아나 살구의 향이 은은하게 퍼지고, 설탕을 입힌 바닐라의 풍미가 입안을 채웁니다.','https://d2wosiipoa41qn.cloudfront.net/w93XnVg9SZoPholKZLBnz5gcYks=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220816/5aab2e0ae86543cf88714a2838fc48e6.png');
insert into coffee_beans values(208,'과테말라 디카페인 마운틴워터 프로세스',18900,100,100,'밸런스_고소','헤이즐넛 같은 고소함과 초콜릿의 단맛이 느껴져요.','https://d2wosiipoa41qn.cloudfront.net/GPX90z90A3b9L7tF2FtgqhNrsxY=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220824/f2b7b9853d62494cbb4308d3c4a21cbe.png');
insert into coffee_beans values(209,'리밀 블렌드',18000,100,100,'고소_쌉살','견과류의 고소함과 카카오의 달콤 쌉싸름한 맛이 느껴져요.','https://d2wosiipoa41qn.cloudfront.net/v4HuPKAeJylu1QcOkegjWSFtswI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220720/fbd08ec02a454297a643842fb0a26288.png');
insert into coffee_beans values(210,'마누 블렌드',18500,100,100,'쌉살_초콜릿','누룽지처럼 고소하고, 다크 초콜릿 같이 쌉싸름 해요.','https://d2wosiipoa41qn.cloudfront.net/4ZsMzw6IF9RA2HgL7qacP_aPrhE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220720/4fcab92db3564dc4b652c8b92d47e0e0.png');
insert into coffee_beans values(211,'룰라바이',13900,100,100,'달큼_상큼','감귤의 시트러스한 단맛을 바탕으로 시나몬과 고구마, 후미에는 꿀의 깊은 맛이 입 안에 남습니다.','https://d2wosiipoa41qn.cloudfront.net/Cm0qNXDPBZOjI1oG9o6JkAK_zm4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/91ddcba5e19f41df9785332a146d3160.png');
insert into coffee_beans values(212,'커넥츠 블렌드',13900,100,100,'밸런스_부드러운','브라운슈가의 단맛을 베이스로 건과일의 향미가 느껴지는 균형감이 뛰어난 스페셜티 블렌딩 입니다.','https://d2wosiipoa41qn.cloudfront.net/AqUz76SSC82EQFHYLjRtT6pTymg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/01075ba4558244f482eb7dfd92dc7944.png');
insert into coffee_beans values(213,'온두라스 엘 푸엔테 워시드',15900,100,100,'달콤_고소','청사과에서 느껴지는 산미로 시작해서 크림 같은 촉감과 캐러멜과 카카오의 쌉싸름한 단맛으로 마무리됩니다.','https://d2wosiipoa41qn.cloudfront.net/YQld5RSz-ANfKdw4YEfk4l7AfTA=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/66fc1e6eb390451ea9c973074d9eddf4.png');
insert into coffee_beans values(214,'호이스트',15900,100,100,'상큼_깔끔한','커피가 가지고 있는 본연의 풍미를 최대한 살리기 위해 미디엄 로스팅 했습니다. 잘 익은 과일의 다채롭고 산뜻한 풍미, 시럽과 같은 촉감을 가진 깨끗한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/5j_cDX1qekCSyjzIX-M2n_IAEe4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/a9777fbc058446bb9360cf7aa6140c8e.png');
insert into coffee_beans values(215,'오버타임',11000,100,100,'고소_묵직한','묵직하고 견고한 바디를 위해 미디엄 다크로 로스팅 했습니다. 말린 과일의 풍미와 부드러운 촉감과 균형감, 달콤한 초콜릿의 풍미로 마무리됩니다.','https://d2wosiipoa41qn.cloudfront.net/PvoWSFjX7fzBnl12YtL1vMItGOs=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/44c2d74efd9449c18f7fe134eefa484d.png');
insert into coffee_beans values(216,'허클베리',12500,100,100,'상큼_초콜릿','크랜베리 처럼 상큼하고, 달콤한 초콜릿의 여운을 느낄 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/CnstkFPNDPYWVtuyX5_fQykqvws=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/db381d2e3ed346b9972228660939cb0a.png');
insert into coffee_beans values(217,'올드타운',12000,100,100,'깔끔한_밸런스','부드러운 단맛이 좋고, 오렌지의 산미가 은은하게 남는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/B71cPsCEqj5mp0JHhGc8j2uQ7-4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/4405aa04c26a4ca196018f49048b9d9b.png');
insert into coffee_beans values(218,'과테말라 라 볼사 워시드',14500,100,100,'달콤_과일','깨끗하면서 부드러운 산미가 일품, 밸런스가 좋아 매일 마셔도 질리지 않아요.','https://d2wosiipoa41qn.cloudfront.net/Qql3bjr6cmIFlgUq2oy_QpVisdY=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/3538fad3450e402b825a070509433a95.png');
insert into coffee_beans values(219,'콜롬비아 칼다스 워시드',13000,100,100,'부드러운_견과류','견과류의 고소한 맛이 중심이 되고, 자두 뉘앙스의 터치로 특별함을 더한 커피예요.','https://d2wosiipoa41qn.cloudfront.net/61_wJFhIpfrnQtmQr-KhJCmDwEI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221205/c8a6b711208748aa828e14d2d88ffe75.png');
insert into coffee_beans values(220,'에티오피아 시다마 카라모 내추럴',12500,100,100,'달콤_과일','74158 품종들로 이루어진 이 커피는 카라모 케벨레 지역에서 발견되었습니다. 가장 높은 고도에 위치해 가장 낮은 일교차를 보이는 시다모 지방의 카라모 지역은 높은 곳에 위치한 지리적 특성으로 커피의 맛과 향미가 더욱 좋고,일반적인 에티오피아 커피보다 붉은 체리의 성숙 기간이 훨신 더 길다는 특징을 가지고 있습니다. 카라모 지역의 커피는 2020년 C...','https://d2wosiipoa41qn.cloudfront.net/SLen2_1HY3-jPVPGTTNU4IM9Au4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221202/c0259ef2fa60412589d80d3e62a32f85.png');
insert into coffee_beans values(221,'프라이데이',16000,100,100,'고소_묵직한','다크 블렌드 커피로, 단맛과 바디감에 초점을 둔 커피입니다. 라와 밀크베이스 커피에도 잘 어울리며, 베이커리, 디저트용 커피로써도 훌륭합니다.','https://d2wosiipoa41qn.cloudfront.net/OTSFdPojHWlKkp37nYGgjzk7so4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/7acc71577a034589a07deee520307d62.png');
insert into coffee_beans values(222,'아이스브레이커',12900,100,100,'밸런스_깔끔한','센터커피의 시그니처 블렌드이며 스페셜티 커피의 맛과 향을 최대한 살린 블렌드 입니다. 스페셜티 전문 카페 및 납품처에 적합합니다. 첫 모금에서 사과, 흑설탕의 플레이버가 느껴지며, 두 번째 모금에서 마카다미아의 고소함과 더불어 자두의 플레이버가 느껴집니다. 바닐라 같은 단맛의 여운이 길게 느껴지며 밸런스가 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/S3N_SMLHgHGL3bA6-6EStA8aCO0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/75adfa152ba4427288e56c848cecf186.png');
insert into coffee_beans values(223,'에티오피아 시다모 두완초 G1 내추럴',13500,100,100,'달콤_깔끔한','블루베리와 열대과일의 은은한 과일향과 클린한 애프터가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/SNKlLVA-dkaVaQeQG2-OfanhmtI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/01156f14932249f1a68c8338dc2817bd.png');
insert into coffee_beans values(224,'알렉세이션',14000,100,100,'밸런스_과일','은은한 향미와 최대한의 단맛이 끌어올려진 에티오피아 원두와 아몬드계열의 고소함,묵직한 바디,깨끗함을 느낄수 있는 과테말라 원두가 조화롭게 섞인 균형감 있는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/s3XuB6aFpRV0V5DZaXtKyWZuQt0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/249da324331c43f49a3df00636fde112.png');
insert into coffee_beans values(225,'콜롬비아 타비 인디오 아나에로빅',15000,100,100,'밸런스_부드러운','타비는 티피카, 버번, 티모르 하이브리드의 교배종입니다. 티모르 하이브리드의 커피 독병 저항력을 가지면서도 티피카와 버번의 좋은 컵 퀄리티를 함께 가지기 위한 목적으로 개발된 품종입니다. 티피카와 버번의 컵노트를 가진 덕분에 sweetness가 뛰어납니다. 타비는 콜롬비아 원주민 언어로 좋다라는 뜻 이라고 합니다. 이름이 가진 의미처럼, 그저 ...','https://d2wosiipoa41qn.cloudfront.net/t-a97_ea2ktUyWD5FoFUvHJ5aLY=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/adc88f0506214e2dad066035b3a555d7.png');
insert into coffee_beans values(226,'콜롬비아 파넬라 스티키 핑크버번',14000,100,100,'상큼_깔끔한','청포도의 산미와 단맛이 함께 느껴져요. 디저트가 필요한 오후에 드시면 좋을 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/gcASkdfNvbXYMUOF2dxDxFkKJtQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/6cbc17bd0e48433e8c646cab066a2c5a.png');
insert into coffee_beans values(227,'라이카의 여름',16000,100,100,'달콤_깔끔한','사계절블랜딩 커피중 하나인 시즌 블랜드커피 라이카의 여름 입니다. 부드럽게 퍼지는 은은한산미에 과일시럽같은 단맛, 라즈베리와 멜론의 향이 어울어지며 시트러스한 라임이 후미에 느껴지는 복합성이 뛰어난 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/Nub_M3h4PUKBEThxFaVlrW12wKo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/7f66dcddc87e44b79d6732420b2e869f.png');
insert into coffee_beans values(228,'안녕잠비',13000,100,100,'상큼_부드러운','과일사탕을 입안에 머금은 듯한 상큼한산미와 청량감이 함께 느껴지며 새콤달콤한 맛과 향을 가진 개성이 뚜렷한 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/gTFRWsZSHT2rXBP9vKvVMg6c9ng=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/732c48d225a8453995e85d243ca0f3d3.png');
insert into coffee_beans values(229,'나이트아울 블렌드',18000,100,100,'밸런스_달콤','갈색이 떠오르는 단맛이 풍부한 블렌드 입니다. 매일 마셔도 좋은 단골 고객을 위한 커피 입니다.','https://d2wosiipoa41qn.cloudfront.net/etdYMBjtzRnnQOKgp0BxBeXiPo4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/1d23265450e54196bacae02b2cf35668.png');
insert into coffee_beans values(230,'오드아이',19000,100,100,'달콤_쌉살','흑설탕같은 진한 단맛이 특징입니다. 라떼 베리에이션에서 특히 독보적인 존재감을 드러냅니다. 그럼블만의 약배전과 강배전의 조합으로 맛을 완성한 새로운 형태의 블렌딩입니다.','https://d2wosiipoa41qn.cloudfront.net/hpT8grwGuTeNF-R8hUME6R0OPEI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/b1587941f84c4215ad47f716f109c719.png');
insert into coffee_beans values(231,'모노톤',19000,100,100,'쌉살_묵직한','깊은 단맛을 품고있어 바닐라시럽이나 초코시럽과 만나면 보다 깊고 풍부한 단맛을 가진 라떼를 맛보실수 있어요.','https://d2wosiipoa41qn.cloudfront.net/Lg3SuSHtcduhwSWwiUPzUHEhb1Q=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220907/5cbb6306c4344afea20b5b3bf39a03cd.png');
insert into coffee_beans values(232,'디카페인 블렌드',12000,100,100,'고소_견과류','마운틴 워터 프로세스 과정을 통해 카페인을 제거하였으나, 맛과 향미는 더욱 풍부한 웨이브온만의 디카페인 커피입니다. 미디엄 바디에 부드러운 질감, 아몬드와 같은 견과류의 고소함과 브라운슈가의 단맛이 매력적인 디카페인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/bov7D-m02jqbn-ykDorFlCmQwPM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/424be106afea40f0ab1e97b10a19a5f3.png');
insert into coffee_beans values(233,'브라질 캄포 알레그레',12500,100,100,'달콤_과일','웨이브온 그린빈 파이오니어팀에서 직접 수입한 브라질 스페셜티 커피인 캄포 알레그레는 다년간 수상 경력의 농장주 Henrique씨가 네추럴 가공한 커피로 다채로운 맛에 깔끔한 클린컵까지 가진 스페셜티 등급의 커피로 여러분을 새로운 커피의 세계로 인도할 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/mPg4EmE6nNhSxfI9jE7rX4msTEI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220812/0c1fe01dec6f49af82d1984ee5b1fa88.png');
insert into coffee_beans values(234,'에티오피아 구지 우라가',12500,100,100,'상큼_과일','에티오피아 구지 우라가는 구지존의 우라가 지역의 커피로 내츄럴 가공되었습니다. 청포도와 감귤과 같은 산미가 매력적이며, 실키한 느낌의 바디감, 그리고 입안 가득 이어지는 아로마가 특징인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/gJiIE4yYgais2c0E3d0O6YREvLo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/4fcf45bdd89942fb9ebd157145b6514d.png');
insert into coffee_beans values(235,'웨이브온 블렌드',12000,100,100,'고소_밸런스','견과류의 고소한 단맛과 은은하게 퍼지는 산미로 밸런스가 좋은 커피입니다. 라떼와도 잘 어울려요.','https://d2wosiipoa41qn.cloudfront.net/dJCiZnP8g0oXHsu6XpAQXJVibow=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/26791b5cf1df4e008fd3388ee3ca5e35.png');
insert into coffee_beans values(236,'66도 블렌',14000,100,100,'밸런스_깔끔한','처음 마셨을 때 밀크 초콜릿의 뉘앙스와 끝에 남는 상큼함이 깔끔하게 잡아주는 블랜딩. 적당한 바디감과 감귤같은 산미가 잘 어우러져 여운이 긴 것이 특징이며 산미를 싫어하시는 분들도 쉽게 접하기 좋은 매력적인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/LJVllbGpnyE-74WRaOnNonOT0wQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220907/2277a85178df4fb3918a82b0edff9a4e.png');
insert into coffee_beans values(237,'33도 블렌드',15500,100,100,'달콤_부드러운','상큼하게 들어오는 청량감과 부드러운 산미가 매력적이고, 입안에 남는 여운이 달달하게 퍼져 기억에 오래 남는 커피입니다. 산미를 선호하지 않는 분들도 과일같은 달콤함에 매력적으로 즐길 수 있는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/7dO-udsZH5eJWNvUPQH90rOPhr0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220907/d57312e46966411cb5d2112495b6a2b0.png');
insert into coffee_beans values(238,'77도 블렌드',13700,100,100,'고소_묵직한','77도 블랜드를 마셨을 때 입안에 감기는 바디감이 훌륭하며 분쇄 하였을 때 고소한 견과류나 기름진 베이컨의 향미가 일품입니다. 커피를 마시고 느끼는 애프터는 고소함으로 입안을 가득 채우며, 끝에 쓴맛보다 고소함이 남아 기분이 좋아지는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/aPg4YnvqpXau9_Foy2hydKoZk7M=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/9760c387c4dd4a05a1a96d9dbc142d13.png');
insert into coffee_beans values(239,'날아올라',18900,100,100,'꽃향기_화사한','단 맛이 특징이며, 자스민 향을 느낄 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/4kVUh2pkIP_FqntU1Xgz8can_is=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/3e2a1356d09c4725afa4f5605ad8e917.png');
insert into coffee_beans values(240,'봄의 제전',18000,100,100,'달콤_과일','꽃향기도 느껴지고, 섬세하고도 달콤한 생기가 느껴지는 커피입니다. 새콤달콤하고 활기가 넘치는 신맛과 잼 같은 풍부하고 끈적하지만 탄력성 있는 촉감을 경험할 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/wgvqR6QMKOwge85H2qWHr8BKUtI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/8099dbf3c8ef4639b5dc59530d5022fb.png');
insert into coffee_beans values(241,'브릴리',18500,100,100,'고소_부드러운','바닐라, 건과일, 견과류, 크림 브륄레, 누룽지 사탕 등의 향미가 두드러집니다. 매일 먹어도 질리지 않는 마치 밥같은 커피로 누가 마셔도 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/PexiLv6nC6Hgw6nD0zcI0vjyd54=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/fdb3c4c883e74e12af6f9e4b0775a7dd.png');
insert into coffee_beans values(242,'비숲',13900,100,100,'쌉살_깔끔한','건자두, 사과와 같은 느낌이며 토피넛, 초콜릿의 단맛을 경험할 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/lfj5NSk-s7HZWUO9Ss2fFE1IBMk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20210910/0aafc3e56ef6484bbd71f562508cef31.png');
insert into coffee_beans values(243,'Seasonal Blend',13900,100,100,'상큼_깔끔한','FELT가 제안하는 계절감입니다. 잘 익은 과일의 맛과 향에 카라멜 같은 단맛이 조화를 이룹니다. 실크 같은 감촉으로 입안에 닿는 에스프레소 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/BITFRR9_0x98v7dz5nndFkKV9kc=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/0cced078a1cf42dcb8f953c4bdffba73.png');
insert into coffee_beans values(244,'Classic Blend',15900,100,100,'밸런스_깔끔한','FELT가 생각하는 커피의 클래식입니다. 시대는 바뀌어도 변하지 않는 것이 있습니다. 밀크와 다크 초콜릿을 넘나드는 단맛에 은은한 산미가 더해져 맛이 단조롭지 않습니다. 견과류의 고소함이 입안을 지그시 눌러주는 균형이 좋은 에스프레소 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/g98KDsgPKhXmDvPynt2y6X6M95o=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/584a0fbc6fdc4bcaa7277c70eb3cc895.png');
insert into coffee_beans values(245,'Classic Plus Blend',15900,100,100,'쌉살_묵직한','FELT가 말하는 클래식 이상의 클래식 입니다. 카카오닙스의 부드러운 쓴맛과 다크 초콜릿의 풍미가 어우러집니다. 묵직하게 입 안에 꽉 차는 강배전 에스프레소 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/VA2kxc_AQultEHpDtQyx4gz8tAM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/47a384d50a1a49049ee78360bcaf770e.png');
insert into coffee_beans values(246,'Decaffeinated Blend',11000,100,100,'고소_부드러운','FELT가 제안하는 카페인이 부담스러운 순간의 대안입니다. 흑설탕과 같은 단맛과 은은한 산미, 고소한 견과류의 풍미가 어우러지며 부드럽게 입안을 채워주는 디카페인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/p740ICzui3YLat6w9fBM0qFMs-A=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220728/e0dddc5fa92540c7acd0f789ca166c95.png');
insert into coffee_beans values(247,'마리모',12500,100,100,'고소_쌉살','식물학 커피의 써드 블렌드인 마리모는 다크초콜릿의 단맛과 쓴맛, 스모키함이 특징입니다. 단맛과 쓴맛이 조화롭게 어우러져 누구나 편하게 마실 수 있습니다.  식물학 커피 로스터스에서 가장 많은 곳에 납품을 하고 있는 블렌드입니다. 그 만큼 많은 분들이 좋아하고, 대중적인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/eVQnQ5BffhqwalU2knSaTcGjy5g=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/3336b81dedfb4b618d6aba2ab12d6d31.png');
insert into coffee_beans values(248,'리들리',12000,100,100,'밸런스_견과류','식물학 커피의 세컨드 블렌드인 리들리는 볶은 견과류 로스티드 아몬드, 호두, 캐슈넛과 같은 고소함이 인상적이며, 입안을 감싸는 달콤함과 묵직한 무게감을 느낄 수 있습니다. 신맛보다는 고소함을 강조하는 리들리는 커피를 즐겨 드시는 모든 분들에게 사랑을 받는 블렌드 입니다.','https://d2wosiipoa41qn.cloudfront.net/XL9RjJMjHM05mB09png4z3SFV6g=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/365f69c1300f4524b0d3a145bd328ab0.png');
insert into coffee_beans values(249,'몬스테라',13000,100,100,'과일_깔끔한','식물학 커피의 시그니처 블렌드인 몬스테라는 잘 익은 베리류의 풍미와 클린한 애프터를 느끼실 수 있는 블렌드 입니다. 기분 좋은 적당한 산미와 단맛 그리고 깔끔함이 특징입니다. 커피를 자주 즐기시고 좋아하시는 분들에게 추천합니다. 커피의 산미와 우유의 고소함이 잘 어우러져 라떼류와 잘 어울리는 커피라 생각하고, 커피를 처음 접하는 분들도 거부감 없이 ...','https://d2wosiipoa41qn.cloudfront.net/Xv_NrtxJ5aybiSJa91EFP_oPlVU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/34cad147bcf147ad8abc7b578e1a5f47.png');
insert into coffee_beans values(250,'과테말라 안티구아 디카페인',14000,100,100,'초콜릿_묵직한','다크초콜릿의 단맛과 묵직한 바디감이 느껴집니다.','https://d2wosiipoa41qn.cloudfront.net/edVq68kJDxs-L227HFxmGfjnt-0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/ee3b9119b25440c29631e59c1b1282c3.png');
insert into coffee_beans values(251,'에티오피아 G1 콩가',15000,100,100,'꽃향기_은은한','상큼한 꽃향기와 블루베리의 단맛이 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/cBSZ8ZAwzy9Ys9wwbPML8uCsaVY=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/a6e03e48dec943369f126bb5c9265e52.png');
insert into coffee_beans values(252,'포스트업',15000,100,100,'고소_쌉살','초콜릿처럼 견고하고 묵직한 단맛과 쌉싸름함을 중심으로 약간의 산미가 조화를 이루어 안정적이고 좋은 여운을 느낄 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/7lo_v-oKuDbngdeUHLepGo0W1s0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/d9640dae26134f4f91e3ba9350b6340d.png');
insert into coffee_beans values(253,'리볼브',16000,100,100,'밸런스_깔끔한','밀크초콜릿, 헤이즐넛의 풍부한 단맛. 감귤류의 은은한 산미와 감미롭고 부드러운 여운의 훌륭한 균형감을 느낄 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/tIPFWuY-zOyCsEOxo67RupicZTk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/60a1e201855b4e209588b63a4969277b.png');
insert into coffee_beans values(254,'BABY BLEND',12500,100,100,'깔끔한_달콤','탄맛이나 날카로운 신맛이 없이 처음부터 끝까지 먹기 편안하고 화려하지 않은 커피입니다. 고소한 브라운슈가, 캬라멜의 부드러운 질감과 풍부한 단맛을 느낄 수 있어요. 블랙커피와 밀크베리에이션을 동시에 고려했어요.','https://d2wosiipoa41qn.cloudfront.net/BtZMN4TzabNgmQeT-b2ywcjVaoM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/016f0efcf4f54ee49f8714e680fa7f12.png');
insert into coffee_beans values(255,'HAUS BLEND',12900,100,100,'묵직한_풍부한','부정적인 맛이 없이 처음부터 끝까지 마시기 편안합니다. 다크 초콜릿의 풍부한 단맛을 느낄 수 있고, 묵직한 바디감과 후미까지 길게 이어지는 진한 커피향을 경험해보세요.','https://d2wosiipoa41qn.cloudfront.net/dUaMfzKAorRthbwLSEqp1XbF9Lc=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/1769b803941c4ff2a0a6dea6ce223e14.png');
insert into coffee_beans values(256,'에티오피아 리무 볼렌소',12900,100,100,'초콜릿_과일','내추럴 프로세싱 특유의 건포도 향미가 선명히 느껴지며, 좋은 클린컵의 부드러운 산미는 오렌지의 향미로 느껴집니다. 부드러운 질감과 묵직한 초콜릿의 단맛이 긴 여운을 남기는 복합적인 향미와 단맛의 여운이 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/zUaQZHE7D5hAZqdDwSoXmxkH63Y=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/f735b11a99824875919cea8edfcd9653.png');
insert into coffee_beans values(257,'STRONG BLEND',14000,100,100,'쌉살_묵직한','고소하고 짙은 농도의 단맛이 특징인 커피입니다. 커피에서 느껴지는 산미가 싫다면 스트롱 블렌드를 드셔보세요.','https://d2wosiipoa41qn.cloudfront.net/LPTBdjXwS24KMyyxdVm8WYwKN2I=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/136083ee70e443a8ad53337ffa0d95dc.png');
insert into coffee_beans values(258,'ESPRESSO 1',13000,100,100,'초콜릿_과일','매일 마셔도 질리지 않는 산미와 단맛의 밸런스가 좋은 커피예요. 부드러우면서도 향이 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/i12FxdRZjlA07YtAMJtQojOfDUM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/ad10e2f88525411aa27776b4b8b8cb29.png');
insert into coffee_beans values(259,'ESPRESSO 2',13600,100,100,'초콜릿_묵직한','라떼를 즐기는 분들이라면 제격인 커피입니다. 탄맛 없이 달콤쌉싸름한 황설탕의 뉘앙스가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/ByGCni2VwcvKbnIZlIcEgH3Q_5Y=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/29aa6e5648f244a3b72d43dcabce5fed.png');
insert into coffee_beans values(260,'ESPRESSO 3s',18000,100,100,'과일_풍부한','한국커피는 원두의 재료가 되는 생두 구매를 위해 매해 산지로 떠납니다. ESPRESSO 3s는 매해 들여오는 생두 중 가장 맛있는 것들을 골라 선보이는 한국커피의 시즈널 블렌드 입니다. ESPRESSO 3s는 과일잼의 단맛과 촉감, 홍차의 풍성한 향이 매력적입니다.','https://d2wosiipoa41qn.cloudfront.net/9YV5bJ_KcYb63vQ_7wMfvDmBuaU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/a192a75dcc934c068283486b19284f1d.png');
insert into coffee_beans values(261,'CLASSIC BLEND',16200,100,100,'달콤_초콜릿','클래식 블렌드는 황설탕의 단맛, 초콜릿 향미, 카카오 닙스같은 쌉싸름함에 은은한 산미까지 느낄 수 있는 커피입니다. 대중적인 취향에 맞춘 부드러운 단맛 베이스의 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/ROyDZNJ4W2cK7dI8qVq1Dy9eY5w=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/f5695d530bdb4672b7d57dc69fb63029.png');
insert into coffee_beans values(262,'리버벨 블렌드 A',12500,100,100,'고소_묵직한','고소하고 부드러운 목넘김과, 밀크 초콜릿의 단맛이 매력 있습니다. 편안하게 데일리 커피로 드시기 좋아요.','https://d2wosiipoa41qn.cloudfront.net/jfOVs8kxiul5OKsNhujo3S-PpPk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/3de0daf044fe4d3d966f452a7ace6e05.png');
insert into coffee_beans values(263,'리버벨 블렌드 B',15000,100,100,'과일_은은한','입안을 은은하게 맴도는 포도류의 산미와 땅콩 호두의 단맛이 조화를 이룹니다.','https://d2wosiipoa41qn.cloudfront.net/Y86YKQgJL_CWwqkScXFSH8As_u4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/a5443306b6ff41aa98c37dea99c7cef9.png');
insert into coffee_beans values(264,'안목 블렌드',14000,100,100,'밸런스_깔끔한','보사노바의 아메리카노에 사용되는 보사노바 대표 블렌드입니다. 은은한 오렌지의 산미와 크리미한 질감이 특징입니다. 단 맛이 잘 어우러진 밸런스가 좋은 블렌드커피로, 대중적인 아메리카노 추출에 적합한 원두입니다.','https://d2wosiipoa41qn.cloudfront.net/74QEQ9Zif_T-FoWkM3iVaBmdEbQ=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/71f1d9a8c9ec4688aaea9e64734856b9.png');
insert into coffee_beans values(265,'문래 블렌드',16000,100,100,'고소_부드러운','문래블렌드는 밀크 베리에이션 블렌드로, 우유와 특히 잘 어울리는 라떼 전용 블렌드 커피입니다. 견과류의 고소함과 딸기의 향긋함이 더해진 산뜻함이 특징입니다.','https://d2wosiipoa41qn.cloudfront.net/0RW416Y27h-bMgH4_VqYwaedje8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/0f4d966369b846619d353671335ff8fb.pn');
insert into coffee_beans values(266,'이탈리안잡 블렌드',13000,100,100,'고소_묵직한','이태리 북부스타일 에스프레소에 대한 오마주로 달콤 쌉싸름한 맛입니다.','https://d2wosiipoa41qn.cloudfront.net/klMDYfnAUGiFVRyqbaiFU2pjkx8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/a00c9e663d0e4fe980e7005d491e49a3.png');
insert into coffee_beans values(267,'클래식 블렌드',18000,100,100,'밸런스_깔끔한','엘카페커피로스터스의 대표 블렌드로 매일 마셔도 질리지 않는 균형잡힌 맛입니다.','https://d2wosiipoa41qn.cloudfront.net/f35_IwBYmeTPwy7dO-axQ4uSIVk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/20c6d2824bda4b6999d254ad6abf4ba5.png');
insert into coffee_beans values(268,'디카페인 올 데이 블렌드',19000,100,100,'쌉살_묵직한','남녀노소 누구나 좋아할 수 밖에 없는, 가을의 향기가 느껴지는 고소하고 마일드한 국민커피입니다.','https://d2wosiipoa41qn.cloudfront.net/f9BmWZ5LzqxvWkz8UaBs6O3I7ps=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/c7847910ed1e4ce2a68bbea31c76f029.png');
insert into coffee_beans values(269,'디카페인 과테말라 블렌드',19000,100,100,'밸런스_부드러운','비옥한 화산 토양의 묵직하고 지속적이면서도 깊은 여운의 향미를 느끼실 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/KLmkJ442PfDDhyYIqtk6wZYsm5U=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/bdca4a28c6774451920b160ccd884439.png');
insert into coffee_beans values(270,'디카페인 에티오피아 블렌드',12000,100,100,'과일_화사한','검붉은 과일의 싱그러움과 믹스베리의 단맛이 어우러지는 조화로운 향미, 깔끔한 목넘김으로 차가운 음료에 잘 어울립니다.','https://d2wosiipoa41qn.cloudfront.net/XgQ02-musSFXnijMQvzr03t3tBU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/aeefe9b59f984aa68fe63d8864419c51.png');
insert into coffee_beans values(271,'상큼한초밥 블렌딩',12500,100,100,'고소_상큼','상큼한 초밥은 상큼함과 고소함이 공존하는 초밥을 모티브로 제작되었습니다. 커피에서 가장 호불호 강한 신맛을 모든 사람이 좋아할만한 대중적인 맛으로 어떻게 변화시킬까? 라는 의문에 평소 즐겨먹던 초밥을 떠올렸습니다. 초밥은 고소한 흰쌀밥에 새초롬한 식초를 더해 감칠맛을 더해주는데, 즉 고소함에 상큼함을 더하면 굉장히 땡기는 신맛이 된다는 걸 깨닫고 커...','https://d2wosiipoa41qn.cloudfront.net/H-z89LLFaNQ_FR0t_n1V7KSAC2E=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/05bd4fb073134f16add20d19f8f403fa.png');
insert into coffee_beans values(272,'고소한쌀밥 블렌딩',12500,100,100,'고소_밸런스','고소한 쌀밥은 우리가 매일 먹는 하얀 쌀밥을 모티브로 제작되었습니다. 어떤 음식과도 잘 어울리는 쌀밥처럼 다양한 디저트와 잘 어울리는 커피. 씹을수록 단맛이 올라오는 쌀밥처럼 고소한 향과 맛에 달콤함이 은은하게 표현되는 커피. 브라질 특유의 고소한 맛, 강배전한 워시드의 씁쓸한 맛, 약간의 신맛과 깔끔하게 떨어지는 뒷맛 모든 맛들이 어느 것 하나 ...','https://d2wosiipoa41qn.cloudfront.net/9TanyysVafG9jaTl76CuA9jv4bE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/21595d59f9d74e889f5c5a16ae308299.png');
insert into coffee_beans values(273,'콜롬비아 시에라 네바다 디카페인',15000,100,100,'밸런스_과일','기존의 디카페인과는 다른 명확한 캐릭터를 느낄 수 있습니다. 청사과의 달콤하고 상큼함이 함께 느껴져 밸런스 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/gvbyo2eey_wlqwHBs5tceWjIDZs=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/57049e2f3b6240a7aebc4ec6f6f991ea.png');
insert into coffee_beans values(274,'과테말라 웨웨테낭고 디카페인',13000,100,100,'밸런스_초콜릿','구운 아몬드 토피의 쌉싸름하고 무거운 단맛을 느낄 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/Vjd3LWqMLO4uo6VWoPeX25oH52E=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/7be9b07b554e4462a1c7a09dc3d0b65e.png');
insert into coffee_beans values(275,'시티, 서울',13500,100,100,'초콜릿_묵직한','고소한 뉘앙스와 다크 초콜릿의 여운이 좋아요. 밸런스가 좋은 묵직한 클래식 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/Nmuaupz6YODJ9igpncmCfwem0Tg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/c3829e63f7ab45d28cc9a4f46b7032f4.png');
insert into coffee_beans values(276,'우일라 디카페인',13900,100,100,'밸런스_고소','말린 무화과, 건자두에 헤이즐넛의 달달한 향미를 더해 밸런스가 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/HfSPs3y3iOZ1P3tQBxMPuP3V91Y=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221107/b1462fbb9c7e42ccb62c1936a1666286.png');
insert into coffee_beans values(277,'콜롬비아 디카페인',12000,100,100,'고소_밸런스','견과류의 고소함과 다크초콜릿, 카카오 닙스의 달콤쌉싸름함이 매력적인 디카페인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/aPwPMWcBeG4BvxOhRz1I4WER-ZE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/1d298aedd771497cb8f95826ed69c67c.png');
insert into coffee_beans values(278,'콜롬비아 디카페인',12000,100,100,'고소_쌉살','비정제 설탕의 단맛과 토스트 맛이 나요. 카카오의 고소한 향미가 느껴집니다.','https://d2wosiipoa41qn.cloudfront.net/U05QC6l2J3lC9i5xdVwak_E-x10=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/0ef57e5b2c4f461d8a510135885d169b.png');
insert into coffee_beans values(279,'디카프리오',12000,100,100,'쌉살_깔끔한','카카오닙스의 달고 쌉싸름한 맛과 몽글몽글한 질감, 그리고 깔끔한 여운을 가진 디카페인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/dEdaMarELv87J-A-OI9tEBllQQ0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/7ab5583be32d403ba47a1774b5331dbc.png');
insert into coffee_beans values(280,'풍요로운 땅',11500,100,100,'쌉살_깔끔한','누구나 편안히 마실 수 있는 커피로, 달콤쌉싸름하고 입 안 마무리는 시원함을 느낄 수 있어요.','https://d2wosiipoa41qn.cloudfront.net/gujWg4ZPfcNfnka_Il_unRE81Kg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/54c08a9ce10c4faeb2ca1be5c645e26e.png');
insert into coffee_beans values(281,'다크우드',12000,100,100,'달콤_과일','다크우드 블렌드는 첼로 선율처럼 묵직하면서도 매끄럽고 오랫동안 입안을 맴도는 여운이 있는 커피 입니다. 농밀한 단맛이 특징입니다.','https://d2wosiipoa41qn.cloudfront.net/1ghOyN-7doWQwSvOAKlUZn73r3I=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/c6b6ca5778514f0d8edf681f35eb5cc8.png');
insert into coffee_beans values(282,'숲',13000,100,100,'밸런스_과일','숲 블렌드는 마치 숲에 들어온 듯한 느낌을 줍니다. 달고나의 달콤 쌈싸름한 맛, 레몬, 민트의 싱그러운 느낌이 조화를 이룹니다. 깊고 묵직하고 달콤하고 시원한 숲 속 공기를 상상해 보세요.','https://d2wosiipoa41qn.cloudfront.net/UuYy8sIYC6OjReFNoOzoDk3bM84=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/4e135399b87e4351a061bd5e66298b1f.png');
insert into coffee_beans values(283,'위WE',13600,100,100,'달콤_부드러운','과테말라와 에티오피아 커피를 블렌딩한 이 커피는 나무사이로 하루스 블렌드 1호 입니다. 초창기에는 과테말라 안티구아와 에티오피아 이르가체프의 조합으로 나무사이로의 "나무"를 따서 나무커피라 불렀고, 지금은 과테말라 우에우에테낭고 와이칸과 에티오피아 달고단 워시드 커피를 블렌딩하여 와이칸(Wakan)과 에티오피아(Ethiopia)의 앞자리를 딴 WE로 ...','https://d2wosiipoa41qn.cloudfront.net/KUUYJZth61M_t-f0KK0Acb5Y62s=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/7611de8d82a44130bddcfb036bf5b92a.png');
insert into coffee_beans values(284,'젠틀레인 디카페인',18000,100,100,'밸런스_달콤','깔끔하며 부드러운 단맛이 좋은 디카페인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/5QreVldX9JoJrU1NWblww1tFokk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220715/e5ab18941f054d5c873ed1056950f67b.png');
insert into coffee_beans values(285,'빈야도르 블렌드',16200,100,100,'상큼_깔끔한','부드러운 촉감과 은은한 산미가 인상적인 블렌드입니다. 복합적인 과일향과 기분좋은 산미, 부드러운 단맛의 조화를 느껴보세요.','https://d2wosiipoa41qn.cloudfront.net/HU1WZw_37Eqs5NaVDPVa9-7tHxw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/fae13070ea00445fb478d4b2ed5a060f.png');
insert into coffee_beans values(286,'르네테르 블렌드',12500,100,100,'밸런스_부드러운','달콤하고 우유와 만났을때 맛, 농도 그리고 촉감이 배가 되는 블렌드 입니다.','https://d2wosiipoa41qn.cloudfront.net/t8a7jZN26x4k4qPMZRpsYSsDU4U=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/e37b12f405384fb3893174f653044f0e.png');
insert into coffee_beans values(287,'에티오피아 베레게티 게이샤 G1 내추럴',15000,100,100,'과일_부드러운','게이샤 특유의 향 보다는 구지 지역의 개성 있는 맛과 열대과일의 향미가 잘 어우러져 기분 좋게 즐길 수 있는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/s2eWzanCmukZ47d2BDllVbeNWzo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/8c1bb821269b45a3bddf2c545519a80b.png');
insert into coffee_beans values(288,'램블 블렌드',14000,100,100,'상큼_달콤','브루잉으로 드시면 차(Tea)를 연상케 하는 깨끗함을, 에스프레소 추출을 통해 즐기시면 커피 본연의 상큼하고 달콤함을 즐기실 수 있습니다. 커피 본연의 과일의 향미, 밝은 산미와 깨끗한 단맛을 느낄수 있는 502커피로스터스의 시그니처 블렌드!','https://d2wosiipoa41qn.cloudfront.net/kXXmaw6HbGg0qsK_tYGhdI58njg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/69540fa9753f4dea847ef80282fdc9de.png');
insert into coffee_beans values(289,'딥씨 블렌드',16000,100,100,'달콤_밸런스','낮은 톤의 은은한 산미와 초콜릿의 깊은 여운을 가진 딥씨는 브루잉부터 에스프레소까지 어떤 종류의 추출에도 잘 어울리는 커피로 균형잡힌 맛과 향을 가지고 있습니다.','https://d2wosiipoa41qn.cloudfront.net/AzPZHmGh5uAbqyNuMxrZ1n7uGp0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/bbb599b7319a42409d8318c4b8db64b8.png');
insert into coffee_beans values(290,'공단향 블렌드',13000,100,100,'달콤_쌉살','“502커피로스터스는 2009년 가산디지털단지에서 시작되었습니다” 지금은 IT산업의 메카인 가산디지털단지, 구로디지털단지가 ‘구로공단’으로 명명되던 힘든 시절이 있었습니다. 구로공단은 노동집약적 섬유, 봉제업체가 주를 이룬 국가 주도 하의 수출산업단지였습니다. 근로자들은 시골에서 상경한 젊은이들로 가난에 밀려 서둘러 가장이 된 우리의 어린 누이와 ...','https://d2wosiipoa41qn.cloudfront.net/u9b0iyJqAJjhyeTIZTt_rtlpT4s=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/3dcd512f7a2c48f3b7625c3d09aaf852.png');
insert into coffee_beans values(291,'클래식 블렌드',18000,100,100,'밸런스_고소','502커피로스터스의 시그니쳐 블렌드 중 가장 오랜 기간 동안 사랑받아 온 클래식은 화려한 매력을 강조하기보다 “견과류의 고소한 향미와 묵직한 바디감”이 주는 탄탄한 베이스로 어떤 베리에이션과도 잘 어울리는 듬직한 커피 입니다. 브루잉, 에스프레소, 그리고 다양한 베리에이션까지 무난하게 소화해내는 클래식은 다재다능한 제너럴리스트로서 어떤 상황에서도 실...','https://d2wosiipoa41qn.cloudfront.net/5BVA4jDFjPuGxV4EL1RCzVDgXlg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/9e7c99dfe4a54f1e9003629ddc5e407f.png');
insert into coffee_beans values(292,'릴리슈슈',19000,100,100,'고소_묵직한','안녕가비의 시그니쳐블랜딩으로 에스프레소 및 우유가 들어가는 다양한 베리에이션과 잘 조화되는 커피입니다. 풍부한 초콜릿의 단맛과 달콤쌉사래함을 크리미하고 묵직한 바디감과 같이 느낄수 있는 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/UP-eMqYe_vjHyFeTj2BWyQYZH-4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/f1cec53028994bc6a7212e64eaf943a4.png');
insert into coffee_beans values(293,'강배전 블렌드',19000,100,100,'묵직한_쌉살','스모키한 향과 묵직한 바디감이 느껴져요.','https://d2wosiipoa41qn.cloudfront.net/3AWK7NBXOqpA3T9-1RiCDHt7krY=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/368e25e9dd294043a7652c4bd354e1c7.png');
insert into coffee_beans values(294,'중배전 블렌드',12000,100,100,'밸런스_묵직한','좋은 밸런스와 달콤쌉싸름한 다크 초콜릿이 떠올라요.','https://d2wosiipoa41qn.cloudfront.net/OqHIvmrJjN8R9rIoGGFjWfDWuHw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/2c343675f7ba4a6399363b31932933ff.png');
insert into coffee_beans values(295,'싸우스폴 블렌드',12500,100,100,'밸런스_깔끔한','다크초콜릿과 카라멜향이 느껴지는 은은한 단맛과 클린함이 느껴집니다.','https://d2wosiipoa41qn.cloudfront.net/HyCobwBDLW4Ym-zb6ZdKxPck3pc=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/5b32b21308684f5f9291009848da1aff.png');
insert into coffee_beans values(296,'과테말라 웨웨테낭고 SHB 디카페인',12500,100,100,'달콤_부드러운','군고구마의 달콤함을 닮은 깨끗한 맛의 디카페인 커피입니다','https://d2wosiipoa41qn.cloudfront.net/xPKul2nO13pKeIIkFvqsDfMhjmo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/7ef60968d93347d7bbebcb78ec70f5f5.png');
insert into coffee_beans values(297,'BACS 시그니처 블렌드',12000,100,100,'화사한_풍부한','풍부하고 다채로운 맛이 특징이며 에스프레소 베이스 음료와 필터 커피에 모두 잘 어울려요.','https://d2wosiipoa41qn.cloudfront.net/AOIe-Ee2HgPbv7aUZk-LMA4VtD0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220914/c82e87bf36e645ec9ff7bd3c157324b2.png');
insert into coffee_beans values(298,'BACS 다크 블렌드',14000,100,100,'고소_밸런스','묵직한 바디감, 약간의 스모키함, 깔끔한 뒷맛이 특징입니다. 에스프레소 베이스 음료와 필터커피에 모두 잘 어울려요','https://d2wosiipoa41qn.cloudfront.net/ZW5hzr0Z_vfvIZ8lqQ_A2bSLUbg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220914/4eb4c0dbdaea4932885e0e2f7d8bf321.png');
insert into coffee_beans values(299,'Blend N.4',15500,100,100,'풍부한_쌉살','구운 너트류의 고소함과 깊은 바디감을 가진 커피. 다크초코렛의 후미까지 느낄 수 있는 단맛 중심의 블렌드 입니다.','https://d2wosiipoa41qn.cloudfront.net/7PgxOkcqwbrG-olW1caF5VVslwc=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/7a5f5565e18d45afb879b14f94cf1c17.png');
insert into coffee_beans values(300,'Blend N.3',13700,100,100,'초콜릿_밸런스','밀크초코렛의 노트가 인상적인 밸런스 커피. 은은한 산미, 초코렛의 단맛, 좋은 밸런스까지 모든면에서 손색없는 블렌드 입니다.','https://d2wosiipoa41qn.cloudfront.net/sWfmeUUk4w7cYcbfxyXu3awt8XI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/0813ca625acf4658bd9ba5111d28f10a.png');
insert into coffee_beans values(301,'진양조',18900,100,100,'고소_견과류','진한 풍미가 좋은 다크로스팅 블렌드. 호두의 너티함을 베이스로 옥수수와 같은 구수한 후미가 인상적인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/ow-DZaQ0dh0vIQZ2Zxo__334AFU=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/33fb62f130d1449b97364a0bf0d88a6d.png');
insert into coffee_beans values(302,'Blend N.1',18000,100,100,'달콤_과일','스탠딩커피의 첫번째 시그니처 블렌드. 바닐라의 단맛을 기반으로 과일의 향과 맛이 매력적인 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/kh7gMBAmIT0DtCAP6j6J3x3BNEk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220908/958ecf5211a146e08409e356d927bc7b.png');
insert into coffee_beans values(303,'디카페인 에티오피아 예가체프 워카 내추럴 스위스 워터',18500,100,100,'고소_묵직한','센터커피는 디카페인은 맛이 없다라는 편견을 깨기위해 퀄리티 좋은 생두 선별에 오직 물로 카페인을 추출하는 스위스 워터 방식의 디카페인 공정을 거친 커피를 센터커피만의 로스팅 기법으로 풀어내었습니다. 커피 본연의 맛과 향을 최대한 살린 센터커피 디카페인 커피는 카페인 섭취에 부담을 느끼시는 분들이 편하고 부담 없이 드실 수 있습니다. 캐러멜의 단맛이...','https://d2wosiipoa41qn.cloudfront.net/6CL4yDECvZUD-8bK8JYM-XcyZO4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220531/ee4fe5d6f74444f981792b22fbeaa350.png');
insert into coffee_beans values(304,'메이데이',13900,100,100,'상큼_깔끔한','메이 데이(May Day)는 근로자의 노고를 위로하기 위한 휴일이라는 뜻으로 한 잔의 커피에 휴식 같은 존재가 되고자 만든 센터커피만의 특별한 시그니처 블렌드 입니다. 데일리 블렌드로 적합한 커피로 밸런스와 복합성에 중점을 둔 커피이며, 매일 마셔도 질리지 않는 커피로는 합리적인 가격의 블렌드 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/0O-4yAN-fMuOsPYibj3Bpnwqk00=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/951f80c3ce154f50be0c181a1dad9279.png');
insert into coffee_beans values(305,'하우스 블렌드',13900,100,100,'밸런스_부드러운','은은한 산미와 밀크초콜릿의 풍미가 달콤하게 다가옵니다. 고소한 여운과 밸런스가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/qWiGhX-wL6Ey_Ho7KcyEJC16cFc=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/74f318337a1a47aa91bd5c111ae9ce14.png');
insert into coffee_beans values(306,'다크 블렌드',15900,100,100,'고소_묵직한','다크초콜릿처럼 달콤하고 쌉싸름한 맛이 느껴져요. 고소하고 묵직한 바디감이 좋습니다.','https://d2wosiipoa41qn.cloudfront.net/FuInn-cQcvHsSBsxVrZLS5u8xi8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/f3324294cb0a473a8a1b6e89392db152.png');
insert into coffee_beans values(307,'에티오피아 아리차 스페셜 G1 내추럴',15900,100,100,'과일_꽃향기','아리차 스페셜은 예가체프 지역내에서도 품질이 매우 우수한 농장으로 아리차에서 생산된 내추럴 프로세싱 커피 중에 가장 품질이 우수한 커피를 따로 선별하여 스페셜로 판매하는 상품입니다. 클린컵이 매우 우수하며 기분 좋은 산미와 각종 베리류와 열대과일의 단맛 그리고 밀크 초콜릿의 바디까지 경험할 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/p9Sno2AStYvD4l3KUBwZN5eCfXg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220715/a98f3b3610b64a329567c2914871fe0d.png');
insert into coffee_beans values(308,'마이 로맨스',11000,100,100,'화사한_꽃향기','마이로맨스는 그 이름에서 느껴지듯이 산뜻하고 부드러운 느낌의 블랜드입니다. 에스프레소 블렌드이지만 브루잉으로도 충분히 즐길 수 있습니다. CQI의 Q-Grader이자 SCA CSP Diploma인 브니엘 커피 로스터스 대표가 직접 생두를 선별하여 정성껏 로스팅 하였습니다. 오렌지의 향긋한 향과 딸기와 라즈베리 그리고 달콤하고 부드러운 마시멜로 같은 ...','https://d2wosiipoa41qn.cloudfront.net/TrUiEV5aq2O8BQ8z_1JHVL9kWpk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220715/5075d9d85ca74e52bd35a5d893733f67.png');
insert into coffee_beans values(309,'젠틀레인 디카페인',12500,100,100,'초콜릿_달콤','CQI의 Q-Grader이자 SCA CSP Diploma인 브니엘 커피 로스터스 대표가 직접 생두를 선별하여 섬세한 로스팅 프로파일로 깊은 단맛과 부드러운 바디, 무엇보다 누텔라의 풍부한 초콜레티를 담아냈습니다. 풍부한 바디와 부드러운 목넘김에 달큰한 단맛은 덤!!','https://d2wosiipoa41qn.cloudfront.net/1-sdogOmb2h_QX8CUUTImQHTXSM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220715/17f28aea878142f9a57f8e11db6679b0.png');
insert into coffee_beans values(310,'어느푸른저녁',12000,100,100,'밸런스_고소','베리류의 산미와 다크 초콜릿과 흑설탕의 단맛을 느낄 수 있습니다. 다양한 향미를 가졌으며 깨끗한 목 넘김과 긴 여운을 느낄 수 있어요.','https://d2wosiipoa41qn.cloudfront.net/U-ByRL73e71P1pnlDF-Jmhvbpls=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20210421/f8532ba4a3f240deb7d089e52123141a.png');
insert into coffee_beans values(311,'센스 앤 센서빌리티',14500,100,100,'상큼_과일','커피 맛에도 이성과 감성이 있다면 어떤 맛일까? 이성과 감성의 조화를 추구하며 사는 것이 인간의 삶이라면, 커피 맛에서 이성과 감성의 조화와 매칭되는 맛이란? 이런 물음에서 출발했습니다. “이성과 감성의 조화 = 산미와 단맛의 조화” 이 단순하면서도 어려운 답을 찾기 위한 여정을 시작했습니다. Bright _ Sweet을 기본 컨셉으로 계속 발전시켜...','https://d2wosiipoa41qn.cloudfront.net/3ywaHGGcJRzKx92t5oZhKOj3zT8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/2da00b9750c54595afb17140d51ebda4.png');
insert into coffee_beans values(312,'비터스윗 라이프 에디션2',13000,100,100,'쌉살_묵직한','보다 높은 바디감과 묵직한 단맛을 원하시는 분들에게 추천합니다. 비터스윗라이프 에디션1과 동일한 블렌딩으로 배전도를 높여 소개합니다.','https://d2wosiipoa41qn.cloudfront.net/a48Il7zM9MeRefL7HvfjZpJE-ys=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/82bfc8910d9742de86ee4ca326db11dc.png');
insert into coffee_beans values(313,'비터스윗 라이프 에디션1',12500,100,100,'초콜릿_고소','커피몽타주의 첫번째 에스프레소 블렌딩, "A Bittersweet Life" 이름에서처럼 쓴맛과 단맛의 조화가 매력적인 블렌딩입니다. 인도와 콜롬비아, 아프리카 생두의 맛을 조화시켜 에스프레소의 풍부한 바디감, 달콤함, 그리고 쌉쌀하면서 달콤한 Aftertaste를 맛보실 수 있습니다. 특히, 우유가 들어간 Variation 음료에 강점이 있는 블...','https://d2wosiipoa41qn.cloudfront.net/N45NbUIPABNIHXMNsgxbsFKhMCo=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220727/d4c63576bcc846d0bde74120034c20d6.png');
insert into coffee_beans values(314,'#55-scale',16000,100,100,'고소_밸런스','메이플 시럽과 고소한 맛이 느껴져요. 산미가 은은해요. 바디감까지 균형감이 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/PpeorTHGlgpziKT64dGlLKSg0qg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/2c017c202d31495dabed7a2691d5bdbd.png');
insert into coffee_beans values(315,'#65-scale',12900,100,100,'달콤_상큼','한 잔의 와인을 머금은 듯 느껴지는 베리의 산뜻한 산미와 깔끔한 끝 맛이 마치 잘 고른 티를 우려 낸 것처럼 풍미가 가득합니다.','https://d2wosiipoa41qn.cloudfront.net/h-ulE-dPAeCql_OBt_vGIrXtdfI=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/21fa1ad90e2247b9ac39ad55c0791de7.png');
insert into coffee_beans values(316,'#45-scale',13500,100,100,'쌉살_묵직한','다크 초콜릿의 쌉싸름한 풍미와 스모키한 바디감으로 마시고 난 뒤 긴 여운을 느낄 수 있어요.','https://d2wosiipoa41qn.cloudfront.net/G9wjvVW69b3JKSINnZUL8kbvRVE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/a4bcd193039145bc90e88d029338a7e0.png');
insert into coffee_beans values(317,'콜롬비아 디카페인',14000,100,100,'초콜릿_고소','언더프레셔가 로스팅한 고품질의 콜롬비아 디카페인 원두입니다. "마운틴 워터 프로세스"를 거쳐 카페인을 제거했음에도 생두 본연의 폭발적인 풍미를 유지하고 있습니다. 미디엄 로스팅 과정을 통해 발현되는 산뜻하고 달콤한 톤의 긴 여운이 특징입니다.','https://d2wosiipoa41qn.cloudfront.net/RioAsCoLr4UOwaGTPiAspUWoRuw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221111/ed31d1fba49e45a7b7c0ecd21d502dac.png');
insert into coffee_beans values(318,'데일리스윗',15900,100,100,'묵직한_쌉살','깊이 있는 단맛이 인상적인 중남미와 아프리카 산지의 생두가 조화롭게 어우러진 하우스 블렌드 입니다. 편안한 커피에 초점을 두고 매일 마셔도 기분좋은 달콤함을 담아냈습니다.','https://d2wosiipoa41qn.cloudfront.net/_YwM9Aw77Ax7Gz5zMFlDZGMnuWg=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221111/b3dc5ae64d4e43f1b4d31fee4bc6d89e.png');
insert into coffee_beans values(319,'브루클린 컴포트',15900,100,100,'과일_꽃향기','상큼한 베리류의 과일 주스처럼 향이 다채로워요. 끝 맛에서 얼그레이 티를 느낄 수 있습니다.','https://d2wosiipoa41qn.cloudfront.net/Dy8-lvzgMIn97ejcpG9ORVSsgq0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221111/61b1df5527424501a09880d3691822b6.png');
insert into coffee_beans values(320,'골든에라',11000,100,100,'풍부한_초콜릿','늘 편하게 마실 수 있는 언더프레셔 시그니쳐 블렌드 입니다. 원두 자체의 풍부한 단맛을 중심으로 부드러운 산미와 균형감있는 바디가 특징입니다.','https://d2wosiipoa41qn.cloudfront.net/jyuJzPrU47Ic7xpOrvoJNfpriGE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221111/f48e64e1e06e45839aa3984ae52c39c5.png');
insert into coffee_beans values(321,'디 카페인 블렌드',12500,100,100,'고소_부드러운','밀크초콜릿과 카라멜의 단맛에 구운 견과류의 고소함이 더해져서, 부드럽고 편안해요.','https://d2wosiipoa41qn.cloudfront.net/KdjZEjJ7_PjoiAjXuE4Q8fOsrGM=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220803/f4a56fff9fb849da9f994c5ce326e504.png');
insert into coffee_beans values(322,'도화 블렌드',12000,100,100,'초콜릿_과일','다크초콜릿 속에 말린 과일의 진한 단맛과 은은한 꽃향기가 남습니다. 레드와인을 닮아 밸런스가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/Wi59z4lmTp00sgpdz0k1WWfG29Y=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/c90744ca368e4d97815238cb516bbb2d.png');
insert into coffee_beans values(323,'B#2',13000,100,100,'밸런스_깔끔한','새콤달콤 잘익은 복숭아의 아로마와 단맛이 긴 여운을 만들며 밸런스가 좋은 블랜드입니다.','https://d2wosiipoa41qn.cloudfront.net/etnxw9CZQ6TywQhaa89EH0uT2kw=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/cd4a10f68a104db09192d31b25a55e06.png');
insert into coffee_beans values(324,'M#1',14000,100,100,'초콜릿_긴여운','구운 견과류의 고소함, 다크 초콜릿의 무게감과 달달함이 느껴지며 밸런스가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/MimUiBNo8UDO_UCWWXZmrhQcnX8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/4d8275a6b0bf4726927106a1438e325b.png');
insert into coffee_beans values(325,'M#2',15000,100,100,'화사한_과일','자두와 체리의 단맛이 진하게 느껴져요. 끝 맛과 여운이 홍차처럼 느껴지며 기분이 좋아요.','https://d2wosiipoa41qn.cloudfront.net/L61DAexZKdbzNGur2zyVlTilfng=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/323de1d68e514e41a2ce15d8b8055371.png');
insert into coffee_beans values(326,'2nd WAVE : Customizing',15000,100,100,'달콤_초콜릿','뉴웨이브의 대표 블렌드로써 감귤 초콜릿을 먹는 듯 균형잡힌 산미와 단맛으로 거부감 없이 입맛을 즐겁게 합니다.','https://d2wosiipoa41qn.cloudfront.net/_ryEEcaLBmsIPGeJi5z0y-04SiY=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220725/68c0fb72de4e49a1947fddff5547082a.png');
insert into coffee_beans values(327,'1st WAVE : Supply',16000,100,100,'밸런스_고소','묵직한 단맛과 카카오의 쓴맛이 입안을 가득 채웁니다.','https://d2wosiipoa41qn.cloudfront.net/YtTKGVWVAU2jkX33njeIVugCBN4=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/0295086108a44df081b90a8150f0d897.png');
insert into coffee_beans values(328,'온두라스 엘 테레로 티피카 풀리 워시드',15900,100,100,'부드러운_밸런스','달콤한 오렌지와 고소함 깔끔하고 깊은 단맛의 피니쉬','https://d2wosiipoa41qn.cloudfront.net/S1Xy3esCszgvLp_NMHbr6E93Fx0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221202/219408d305d644068362f6fa430d3e81.png');
insert into coffee_beans values(329,'필베러',11000,100,100,'과일_밸런스','자두, 복숭아에서 느껴지는 달콤한 산미와 갈색설탕 단맛으로 밸런스가 굉장히 좋은, 베러로스팅랩의 인기 블렌드입니다. 블렌드명처럼 드셔보시면 기분이 좋아질 것 입니다.','https://d2wosiipoa41qn.cloudfront.net/vkT4AGBV-0N1pAYMfu0T4Tww3aA=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220705/3a2223222e7d4e1491a62c3e2ed80272.png');
insert into coffee_beans values(330,'레스벗베러',12500,100,100,'고소_견과류','블렌드명처럼 단순하고, 심플하지만 우리에게 가장 익숙한 대중적인 맛을 추구하고 있습니다. 중남미 생두로 구성해 고소하며 카라멜 단맛이 느껴지고, 다크 초콜릿의 묵직한 바디감이 느껴져요.','https://d2wosiipoa41qn.cloudfront.net/EtCkVnUCIGN4pbqWhJTLpi18WRk=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/7460d021955c4ea8956b5a2e28f61ee7.png');
insert into coffee_beans values(331,'보이져',12000,100,100,'달콤_화사한','무르익은 과일의 산미가 달콤해요. 풍부한 꽃 향이 매력적인 스티머스의 시그니처 블렌드입니다.','https://d2wosiipoa41qn.cloudfront.net/9_7LSkdIUIKSJgjd9kJtXaWPhs8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221107/95a67026ebb14a35abcd39fc1ea90d51.png');
insert into coffee_beans values(332,'플레이어 (구.올드브릿지블렌드)',14500,100,100,'밸런스_고소','밀크초콜릿의 진한 단맛이 특징이예요. 견과류의 고소함, 부드러운 산미가 조화롭게 어우러져요.','https://d2wosiipoa41qn.cloudfront.net/KTgywlJ9K3RtnNdkN8piWEX0-Q8=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20221107/70ea9b58c7e6427daafe13156cea91b8.png');
insert into coffee_beans values(333,'엘 카미노',13000,100,100,'고소_견과류','고소하고, 묵직하지만, 단맛이 잘 느껴져요. 마실 때 이물감이 없이 클린컵도 좋은 커피입니다.','https://d2wosiipoa41qn.cloudfront.net/n6bToapnmOAzK_xz7YfOqraeSX0=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/5d69b081653a41d7bedcd9a23bc2b89f.png');
insert into coffee_beans values(334,'파나마 만다리나 게이샤 허니 슬로우',12500,100,100,'화사한_꽃향기','베리, 복숭아의 과일 향미가 풍부해요. 함께 밀크 초콜릿, 카카오의 향미가 더해져 밸런스가 좋아요.','https://d2wosiipoa41qn.cloudfront.net/3ynfi050aQRo_1aRXsf7totiIQc=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220913/b9e45375e88c4d5a93cbd4c97e2b6310.png');
insert into coffee_beans values(335,'매뉴얼',16000,100,100,'밸런스_고소','아메리카노, 라떼 모두 잘 어울리는 블렌딩입니다. 부드럽고 은은하게 올라오는 산미와 밀크 초콜릿, 그리고 고소함을 입안에 풍부하게 안겨줍니다.','https://d2wosiipoa41qn.cloudfront.net/ESgxlxbcD_wGfTTycyKSaLv9lgE=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/5b9b985f523f41af85bb6e40e40dac44.png');
insert into coffee_beans values(336,'콜린',12900,100,100,'달콤_고소','잘 익은 체리의 농후한 단맛과 고소한 아몬드 캐러멜이 입안 가득 남는 끝 맛을 느낄 수 있어요. 블렌딩에 최적화된 좋은 생두를 선별하고, 일관성 있고 논리적인 로스팅을 추구합니다. 결점두를 직접 선별하여 패키징 하는 등 최상의 품질을 위해 노력합니다.','https://d2wosiipoa41qn.cloudfront.net/pODfJ_X7dPYRd0GtyXv3s5jSibA=/650x650/s3.ap-northeast-2.amazonaws.com/koke-uploads/images/20220602/879c7a36de0942648a06f429d077ada5.png');
